void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;

    float dist = (1.0 - abs(uv.y - 0.5 - 0.5 * sin(uv.x * 3.141  * 8.0 + iTime * 10.0) * (1.0 - abs(uv.x - 0.5) * 2.0))) * (1.0 - abs(uv.x - 0.5) * 2.0);

    fragColor = vec4(1.0*cos(iTime+uv.xxx+vec3(0,2,4))*dist,1.0);
}

#define SAMPLES 16
#define EPSILON 0.01
#define DEPTH_REVERSED true
#define SHADOW_INTENSITY 5.
#define LIGHT_Z_OFFSET 0.1
#define SOFT_SHADOWS .25
#define DEPTH_SCALE .5

float depth(vec2 coords)
{
    if(DEPTH_REVERSED) {
        return 1. - texture(iChannel0, coords).x * DEPTH_SCALE;
    } else {
        return texture(iChannel0, coords).x * DEPTH_SCALE;
    }
}


float diffuse(vec3 n, vec3 l)
{
	return max(0.0, dot(n,l));
}

vec3 normal(vec2 coords, float intensity)
{
    float offset = 0.0025;
    vec3 a = vec3(coords.x - offset, 0.0, depth(vec2(coords.x - offset, coords.y)) * intensity);
    vec3 b = vec3(coords.x + offset, 0.0, depth(vec2(coords.x + offset, coords.y)) * intensity);
    vec3 c = vec3(0.0, coords.y + offset, depth(vec2(coords.x, coords.y + offset)) * intensity);
    vec3 d = vec3(0.0, coords.y - offset, depth(vec2(coords.x, coords.y - offset)) * intensity);

    return normalize(cross(b-a, c-d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouseuv = iMouse.xy / iResolution.xy;

    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    mouseuv.x *= aspect;

    vec3 norm = normal(uv, 0.2);

    vec3 colour;

    vec3 fragPos = vec3(uv, depth(uv));
    vec3 lightPos = vec3(mouseuv, depth(mouseuv) - LIGHT_Z_OFFSET);
    vec2 dir = normalize(fragPos.xy - lightPos.xy);

    float traverse_by = 1. / float(SAMPLES);
    for(float i = 0.; i < 1.; i += traverse_by) {
        vec3 traversedPos = fragPos + (lightPos - fragPos) * i;
        float traversedDepth = depth(traversedPos.xy);

        float diff = traversedPos.z - traversedDepth;
        if(diff > EPSILON) {
            colour += vec3(diff) * traverse_by / SOFT_SHADOWS;
        }
    }

    colour *= SHADOW_INTENSITY;
    fragColor.rgb = vec3(diffuse(normalize(fragPos - lightPos), norm));
    float ambient = diffuse(norm, vec3(0.0,0.0,1.0))*0.05;
    fragColor += ambient;
    fragColor.rgb *= 1. - colour;
    fragColor.a = 1.;
}
