// https://iquilezles.org/articles/distfunctions2d/
float sdRoundedBox( vec2 p, vec2 b, vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }


// straight
float ramp(float t, float inMin, float inMax, float outMin, float outMax) {

    return clamp(mix(outMin, outMax, t), inMin, inMax);

}

float map(vec2 p) {

    float l = 0.001;

    float b1 = sdRoundedBox(p+vec2(0.2, 0.), vec2(.15,.25), vec4(0., 0., 0.1, 0.));
    float b2 = sdRoundedBox(p-vec2(0.2, 0.), vec2(.15,.25), vec4(0.025, 0., 0., 0.));
    float b3 = sdRoundedBox(p-vec2(0., 0.), vec2(.3,.1), vec4(0.));

    float res = opSmoothUnion(b1, b2, 0.);
    res = opSmoothUnion(res, b3, 0.);

    return res;

}

vec2 Gradient(vec2 p) {

    float epsilon = 0.0001;
    vec2 dx = vec2(epsilon, 0.);
    vec2 dy = vec2(0., epsilon);

    float ddx = map(p + dx) - map(p - dx);
    float ddy = map(p + dy) - map(p - dy);

    return vec2(ddx, ddy);

}

vec2 unitGradient(vec2 p) {

    float epsilon = 0.0001;
    vec2 dx = vec2(epsilon, 0.);
    vec2 dy = vec2(0., epsilon);

    float ddx = map(p + dx) - map(p - dx);
    float ddy = map(p + dy) - map(p - dy);

    return normalize(vec2(ddx, ddy));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
