    vec3 col2 = vec3(180.) / 255.;

    float d = map(uv);
    vec2 g0 = unitGradient(uv);

    // pre-compensation
    vec2 pRComp = vec2(uv.x, uv.y - 0.03 * sign(g0.y));
    float dComp = map(pRComp);

    // gradient
    vec2 g = Gradient(pRComp);
