    // translucent original shape
    backgroundColor = mix(backgroundColor, backgroundColor * vec4(0.55, 1., 0.8, 1.), smoothstep(1.0,0.0,d * 255.));

    // translucent compensated shape
    backgroundColor = mix(backgroundColor, backgroundColor * vec4(1., 0.5, 0.8, 1.), smoothstep(1.0,0.0,dComp * 255.));

    // solid final shape
    fragColor = mix(solidColor, backgroundColor, smoothstep(0.0,1.0,final * 255.) );

