// regular N-gon, if you want circle, let N = 0
const int N0 = 5;
const int N1 = 6;

const float ANGLE = PI / 4.0;
const float THICKNESS = 0.01;

vec2 feetRegularPolygon(in vec2 p, in int n, in float r) {
    vec2 h;
    float dist = 10.0 * r;
    for (int i = 0; i < n; i++) {
        float t0 = 2.0 * PI * float(i) / float(n),
        t1 = 2.0 * PI * float(i + 1) / float(n);
        vec2 a = vec2(-sin(t0), cos(t0)) * r,
        b = vec2(-sin(t1), cos(t1)) * r,
        ab = b - a, ap = p - a,
        hab = ap - clamp(dot(ap, ab) / dot(ab, ab), 0.0, 1.0) * ab;
        float dist0 = length(hab);
        if (dist0 < dist) {
            dist = dist0;
            h = hab;
        }
    }
    return p + h;
}

vec2 feetCircle(in vec2 p, in float r) {
    if (length(p) == 0.0) return vec2(0, r);
    else return r * normalize(p);
}

vec2 feet0(in vec2 p) {
    if (N0 == 0) return feetCircle(p, 0.5);
    return feetRegularPolygon(p, N0, 0.5);
}

vec2 feet1(in vec2 p) {
    if (N1 == 0) return feetCircle(p, 0.5);
    return feetRegularPolygon(p, N1, 0.5);
}

float sDistCone0(in vec3 p) {
    Camera cam = Camera(
        vec3(0, 0, -1),
        vec3(0, 0, 1),
        vec3(0, 1, 0),
        ANGLE,
        1.0
    );
    vec2 uv = 2.0 * screenUV(cam, p) - 1.0,
    h = feet0(uv);
    float signum = sign(distance(uv, h) - THICKNESS / 2.0);
    h = h + normalize(uv - h) * THICKNESS / 2.0;
    Ray ray = cameraRay(cam, (h + 1.0) / 2.0);
    vec3 op = p - ray.origin;
    return signum * length(op - dot(op, ray.direction) * ray.direction);
}

float sDistCone1(in vec3 p) {
    Camera cam = Camera(
        vec3(0, 0, 1),
        vec3(0, 0, -1),
        vec3(0, 1, 0),
        ANGLE,
        1.0
    );
    vec2 uv = 2.0 * screenUV(cam, p) - 1.0,
    h = feet1(uv);
    float signum = sign(distance(uv, h) - THICKNESS / 2.0);
    h = h + normalize(uv - h) * THICKNESS / 2.0;
    Ray ray = cameraRay(cam, (h + 1.0) / 2.0);
    vec3 op = p - ray.origin;
    return signum * length(op - dot(op, ray.direction) * ray.direction);
}

float sDist(in vec3 p) {
    float dist0 = max(-0.5 - p.z, sDistCone0(p));
    float dist1 = max(p.z - 0.5, sDistCone1(p));
    return max(dist0, dist1);
}

vec3 calcNormal(vec3 p) {
    const vec2 e = vec2(1.e-4,0);
    return normalize(vec3(
        sDist(p + e.xyy) - sDist(p - e.xyy),
        sDist(p + e.yxy) - sDist(p - e.yxy),
        sDist(p + e.yyx) - sDist(p - e.yyx)
    ));
}

vec3 color(vec3 p, vec3 normal, vec3 dir) {
    return vec3(-dot(dir, normal));
}

void mainImage0(out vec4 O, in vec2 U) {
    vec2 r = iResolution.xy;
    float t = mod(iTime, 4.0 * PI);
    if (t < 2.0 * PI) t = max(t - PI, 0.0);
    else t = PI + max(t - 3.0 * PI, 0.0);
    vec3 camPos = vec3(sin(t), 0.6 * sin(t), cos(t));
    Camera cam = Camera(
        camPos,
        -normalize(camPos),
        vec3(0,1,0),
        PI / 6.0,
        r.x / r.y
    );
    Ray ray = cameraRay(cam, U / r);
    vec3 ro = ray.origin, rd = ray.direction, p;

    float dist = 0.0, dist0;
    const float MAX_DIST = 2.;
    for(int i = 0; i++ < 255
        && (dist0 = sDist(p = ro + dist * rd)) > 1.0e-5
        && (dist += dist0) < MAX_DIST;); 

    vec3 col = vec3(0);
    if (dist < MAX_DIST) col = color(p, calcNormal(p), rd);

    O = vec4(col, min(dist, MAX_DIST));
}

void mainImage(out vec4 O, vec2 U) {
    mainImage0(O,U);

