}

vec2 rotate(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat2(c, s, -s, c);
}

float map(vec3 p) {
    for (int i = 0; i < 66; ++i) {
        float t = iTime * 0.006;
        p.xz = rotate(p.xz, t);
        p.xy = rotate(p.xy, t * 1.0);
        p.xz = abs(p.xz);
        p.xz -= 0.16;
    }
    return dot(sign(p), p) / 6.0;
}

vec4 rm(vec3 ro, vec3 rd) {
    float t = 1.0;
    vec3 col = vec3(0.006);
    float d;
    for (float i = 0.0; i < 36.0; i++) {
        vec3 p = ro + rd * t;
        d = map(p) * 0.98;
        if (d < 0.07) {
            break;
        }
        if (d > 22.0) {
            break;
        }
        col += palette(length(p) * 0.1) / (222.0 * d);
        t += d;
    }
    return vec4(col, 1.0 / (d * 20.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);
    float xy = uv.x / uv.y;

    float threshold = 2.0;

    if (abs(xy) < abs(threshold)) {
        vec3 ro = vec3(55.0, 55.0, -13.0);
        ro.xz = rotate(ro.xz, iTime);
        vec3 cf = normalize(-ro);
        vec3 cs = normalize(cross(cf, vec3(7, 3.0, 17.0)));
        vec3 cu = normalize(cross(cf, cs));

        vec3 uuv = ro + cf * 3.0 + uv.x * cs + uv.y * cu;
        vec3 rd = normalize(uuv - ro);

        vec4 col = rm(ro, rd);

        fragColor = col;
    } else {
        vec3 ro = vec3(55.0, 55.0, -13.0);
        ro.xz = rotate(ro.xz, iTime);
        vec3 cf = normalize(-ro);
