        vec3 cu = normalize(cross(cf, cs));

        vec3 uuv = ro + cf * 3.0 + uv.x * cs + uv.y * cu;
        vec3 rd = normalize(uuv - ro);

        vec4 col = rm(ro, rd);

        fragColor = col;
    }
}

// Base calculation on point - line distance: 
// 
// (p - a) - dot((p - a), n) * n
//
// (https://en.wikipedia.org/wiki/Distance_from_a_point_to_a_line)
//
// The distance can never be longer than the segment length
// The distance can never be shorter than minDist (avoids division by zero case) 

float dist2Segment2(vec2 a, vec2 b, vec2 p, float minDist) {
    vec2 ab = b - a;
    vec2 ap = p - a;
    float lAb = max(minDist, length(ab)); // ensure minDist
    vec2 n = ab / lAb;
    float l = clamp(dot(ap, n), 0., lAb); // clamp to segment length
    return length(ap - (l * n)); 
}

