/* Decode color from compressed to linear space.
Do this 1st to image textures before using them.
Images are encoded in the nonlinear srgb color space.
The rgb components need to be linear for filters and effects. */
vec3 rgb(vec4 srgb) { return mix(srgb / 12.92, pow((srgb + .055) / 1.055, vec4(2.4)), step(.04045, srgb)).rgb; }


/* Prepare the gamma for a computer monitor.
Do this after processing and before sending to the display.
Display adapters expect a nonlinear correction of 1 / 2.2 */
vec4 gamma(vec3 rgb) { return vec4(pow(rgb, vec3(1) / 2.2), 1); }


/* Map 3D color to 1D greyscale using CIE standards from 2002 (as opposed to the commonly used 1976 method).
The impact of surroundings is 59% for viewing television.
The average relative luminance of an image field is 20% for typically lit scenes.
Eye cone responsivities relative to srgb & rescaling for perceptually uniform lightness gradient */
float lightness(vec3 rgb) { return pow(dot(rgb, vec3(.2126, .7152, .0722)), .59 * (1.48 + sqrt(.2))); }


/* Demonstration of color management from image to display output. */
void mainImage(out vec4 pixel, vec2 uv) {
    vec4 raw = texture(iChannel0, uv /= iResolution.xy);

    // almost always start by linearizing the compressed rgb texture
    vec3 colorful = rgb(raw),
         greyscale = vec3(lightness(colorful));

    // click to drag color|grey boundary
    uv = round(uv + mix(.5 * sin(-iTime), .5 * (iResolution.x - 2. * iMouse.x) / iResolution.x, max(0., sign(iMouse.z))));
    vec3 mixed = mix(
        // correct
        mix(colorful, greyscale, uv.x),
        // incorrect
        mix(raw.rgb, vec3(length(raw.rgb) / sqrt(3.)), uv.x),
        // hold shift to see incorrect render
        texelFetch(iChannel1, ivec2(16, 0), 0).x
    );

    // almost always end by converting from abstract linear image to hardware scale
    pixel = gamma(mixed);
}


