bool in_rect(vec2 center, vec2 size, vec2 coord){
    return center.x <= coord.x && coord.x <= size.x && center.y <= coord.y && coord.y <= size.y;
}

bool in_circ(vec2 origin, float radius, vec2 coord){
    return length(origin - coord)<=radius;
}

vec2 closest_point(vec2 origin, vec2 center, vec2 size){
    float x = max(center.x, min(origin.x, size.x));
    float y = max(center.y, min(origin.y, size.y));
    return vec2 (x,y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 coord = fragCoord.xy/iResolution.xy*vec2(1920,1080);

    vec2 center = vec2(300.,300.);
    vec2 size = center + vec2(600.,300.);

    vec2 o = iMouse.xy/iResolution.xy*vec2(1920,1080);
    float r = 150.;

    vec2 cp = closest_point(o, center, size);

    if (in_rect(center, size, coord)){
        fragColor = vec4(0.0, 0.0, 1.0, 0.0);
    }
    if (in_circ(o, r, coord)){
        if (length(o - cp) <= r){
            fragColor = vec4(1.0,0.0,0.0,0.0);
        }
        else{
        fragColor = vec4(0.0,1.0,0.0,0.0);
        }
    }
     if (in_circ(cp, 10., coord)){
        fragColor = vec4(1.0);
    }
}

