  return mat2(ca,sa,-sa,ca);
}



float hash11(float t) {
  return fract( sin(t * 7361.994) * 4518.442);
}

vec3 lattice (float t ) {



  float m = t;
  float mx = floor((m-2.)/3.);
  float my = floor((m-1.)/3.);
  float mz = floor((m-0.)/3.);

  float n= m+1.;
  float nx = floor((n-2.)/3.);
  float ny = floor((n-1.)/3.);
  float nz = floor((n-0.)/3.);


  vec3 a =  
  mix(
      vec3(
          hash11(mx)-.5,
          hash11(my)-.5,
          hash11(mz)-.5
      ),
      vec3(
          hash11(nx)-.5,
          hash11(ny)-.5,
          hash11(nz)-.5
      ) , 
      fract(t)
  );
  return a;
}



float hash31(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float hash21(vec2 p2)
{
	p2  = fract(p2 * .1031);
    p2 += dot(p2, p2.xy + 33.33);
    return fract((p2.x + p2.y));
}

float closestWall(vec3 p) {
    vec3 d = min(fract(p), 1.0 - fract(p));
    return min(min(d.x, d.y), d.z);
}

float box (vec3 p, vec3 s) {
    vec3 q = abs(p) - s;
   // return max(max(q.x,q.y),q.z);
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float hash( vec2 p )
{
	float h = dot(p,vec2(127.1,311.7));
    return -1.0 + 2.0*fract(sin(h)*43758.5453123);
}



float field (vec3 p, float scale) {

   p *= scale;
   vec3 id = floor(p)-.5; 
   p = fract(p) -.5;

   float f1 = hash31(id);


   float ball = box(p, vec3(f1 * .4));


   // important detail
   float bound = closestWall(p+.5);

   return min(bound + .1, ball)/scale;
}
float map (vec3 p){
    // geo
    float pos = p.y+1.;

    for (float i = 1.; i < 8.; i++) {
        pos = max(pos, -field(p,pow(2.,i)/16.));
    }

    return pos;
}

vec3 norm(vec3 p) {
  vec2 off=vec2(0.0001,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);


  float tt = iTime;

  // nav



  vec3 s = vec3(0.,3.,-6);
  vec3 t = vec3(0,-1,0);

  vec3 fwd = vec3(0,0,3) * iTime;

  s += fwd;
  t += fwd;

  s += lattice(tt)*7.;
  t += lattice(tt+3.9)*7.;





  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=-normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz);


  vec3 p=s;
  float i=0.;
  float dd = 0.;
  float d;

  // march

  bool hit = false;
  for(float z=0.; z <100.; ++z) {
    i = z;
    d=abs(map(p));
    if(d<.01) {
      hit = true;
      break;
    }
    if ( dd > 100.) break;
    dd += d;
    p+=r*d;
  }


  // color

  vec3 n = norm(p);


  r = reflect(n,r);
  float factor = length(sin(r*3.))/sqrt(3.);
  vec3 col = pow(factor,11.) * vec3(4);


  fragColor = vec4(col, 1);
}

// https://iquilezles.org/articles/palettes/
vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b * cos(6.28318 * (c * t + d));
}

vec4 applyAlakazan(vec2 uv0) {
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv0 = fract(uv0 * 1.5) - 0.5;

        float d = length(uv0) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i * 0.4 + iTime * 0.4);

        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    return vec4(finalColor, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Convert pixel coordinates to a normalized range (-1 to 1)
    vec2 p = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    // Calculate the value of xy using your equation
    float xy = p.x / p.y;

    // Define a threshold for plotting the point
    float threshold = 2.0;

    if (abs(xy) < abs(threshold)) {
        vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

        fragColor = applyAlakazan(uv);
    } else {
        int i;
        for (vec2 c = iResolution.xy, u = (roundEven(fragCoord) - c * 0.5) / c.y, p, z = p + 2e1;
             z.x < 1e2 && (i = int(p) ^ int(p.y) ^ int(z)) % 93 % 43 < 32;
             p = u * z + iTime * vec2(2, 9)) {
            c = fract(-p * sign(u)) / abs(u);
            z += min(min(c, c.yx), fract(-z)) + 2e-5;
            fragColor.rgb = (1e2 - z.x) * (2.0 - cos(vec3(i /= 3, i + 5, i + 4)));
        }

        fragColor /= 2e2 + fwidth(fragColor.g) * 5e2;
    }
}

