void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert pixel coordinates to a normalized range (-1 to 1)
    vec2 p = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);

    // Calculate the value of xy using your equation
    float xy = p.x / p.y;

    // Define a threshold for plotting the point
    float threshold = 2.; // Adjust this value as needed // air: 1.0, water: 1.33, glass: 1.5, diamond: 2.4

    // Check if xy is within the threshold to plot the point
    if (abs(xy) < abs(threshold))
    {
            // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
    }
    else
    {
    // Thanks to "hash: visualising bitplanes" by hornet https://www.shadertoy.com/view/lt2yDm

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uint bit = uint(8.0 * uv.x) + 8u * uint(4.0 * uv.y);

#ifdef BITPLANE
    vec2 seed = mod(fragCoord, vec2(iResolution.x/8.0, iResolution.y/4.0));
#else
    vec2 seed = fragCoord;
#endif

#ifdef ANIMATE
    seed += 100.0 * iTime;
#endif

    uvec3 hash = hash(seed);

#ifdef BITPLANE
#ifdef COLOR
    fragColor = vec4((hash >> bit) & 1u, 1.0);
#else
    fragColor = vec4(vec3(float((hash >> bit) & 1u)), 1.0);
#endif
#else
#ifdef COLOR
    fragColor = vec4(vec3(hash) * (1.0/float(0xffffffffu)), 1.0);
#else
    fragColor = vec4(vec3(float(hash) * (1.0/float(0xffffffffu))), 1.0);
#endif
#endif

#ifdef GRID
    fragColor *= step( 10.0/iResolution.x, 1.0-abs(2.0*fract(8.0*uv.x)-1.0));
    fragColor *= step( 10.0/iResolution.y, 1.0-abs(2.0*fract(4.0*uv.y)-1.0));
#endif
    }
}

