// BBS-inspired hash
//  - Olano, Modified Noise for Evaluation on Graphics Hardware, GH 2005
uint bbs(uint v) {
    v = v % 65521u;
    v = (v * v) % 65521u;
    v = (v * v) % 65521u;
    return v;
}



// CityHash32, adapted from Hash32Len0to4 in https://github.com/google/cityhash
uint city(uint s)
{
    uint len = 4u;
	uint b = 0u;
    uint c = 9u;

    for (uint i = 0u; i < len; i++) {
    	uint v = (s >> (i * 8u)) & 0xffu;
        b = b * c1 + v;
        c ^= b;
    }

    return fmix(mur(b, mur(len, c)));
}

// CityHash32, adapted from Hash32Len5to12 in https://github.com/google/cityhash
uint city(uvec2 s)
{
    uint len = 8u;
    uint a = len, b = len * 5u, c = 9u, d = b;

    a += bswap32(s.x);
    b += bswap32(s.y);
    c += bswap32(s.y);

    return fmix(mur(c, mur(b, mur(a, d))));
}

// CityHash32, adapted from Hash32Len5to12 in https://github.com/google/cityhash
uint city(uvec3 s)
{
    uint len = 12u;
    uint a = len, b = len * 5u, c = 9u, d = b;

    a += bswap32(s.x);
    b += bswap32(s.z);
    c += bswap32(s.y);

    return fmix(mur(c, mur(b, mur(a, d))));
}

// CityHash32, adapted from Hash32Len12to24 in https://github.com/google/cityhash
uint city(uvec4 s)
{
    uint len = 16u;
    uint a = bswap32(s.w);
    uint b = bswap32(s.y);
    uint c = bswap32(s.z);
    uint d = bswap32(s.z);
    uint e = bswap32(s.x);
    uint f = bswap32(s.w);
    uint h = len;

    return fmix(mur(f, mur(e, mur(d, mur(c, mur(b, mur(a, h)))))));
}



