        q = mirror(q, 5.0);
        q = q * s;
        q = translate(q, vec2(0.0, -rSmaller));
        float d = sdTriangleIsosceles(vec2(w / 2.0, -h), q) / (s * z);
        float b = length(uv);
        col = draw(vec3(0.0), d, 8.0 * b, 4.0 * sqrt(b) * col);
        fragColor = vec4(col, 1.0);
    }
}

