const float pi=3.14159265358979;

// Refractive index.
//   1.0        - vacuum
//   1.000293   - air at 0 Â°C, 1 atm
//   1.333      - water at 20 Â°C
//   1.458      - fused quartz
//   1.458..1.7 - typical glass
//   2.417      - diamond
const float n=1.5;

// Balls (position, radius).
// AB is microscope, AC is telescope.
vec4 A,B,C;

vec3 ray_dir(vec3 camera,vec3 target,vec3 up,vec3 xyz)
{
    vec3 z=normalize(target-camera),x=normalize(cross(up,z)),y=cross(z,x);
    return normalize(xyz.x*x+xyz.y*y+xyz.z*z);
}

vec3 sky(vec3 ro,vec3 rd)
{
    vec3 ret=vec3(0.0,0.25,0.5)+vec3(0.125,0.25,0.25)*exp(-3.5*max(rd.y,0.0));
    float m=73.3;
    vec2 v=vec2(atan(rd.z,rd.x)/pi,rd.y);
    mat2 q=mat2(0.8,-0.6,0.6,0.8);
    for(int i=0;i<8;++i)
    {
        vec2 c=floor(m*q*v);
        c.x+=0.5*sin(1.7*c.y+float(i));c.y+=0.5*sin(3.7*c.x);
        c.x+=0.5*sin(3.7*c.y);c.y+=0.5*sin(3.7*c.x+float(i));
        c.x+=0.5*sin(7.3*c.y);c.y+=0.5*sin(7.3*c.x);
        v=c*q/m;
        v.y=clamp(v.y,-1.0,+1.0);
        vec3 n=vec3(sqrt(1.0-v.y*v.y)*vec2(cos(pi*v.x),sin(pi*v.x)),v.y).xzy;
        vec3 d=n-rd;
        ret+=exp(-2.5+sin(3.7*float(i*i))-1e5*dot(d,d));
        q=q*q;
    }
    return ret;
}

vec3 ground(vec3 ro,vec3 rd)
{
    vec3 v=(ro-(ro.y/rd.y)*rd);
    vec2 xy=5.0*mat2(0.8,-0.6,0.6,0.8)*v.xz;
    vec3 ret=vec3(0.0);
    float K=16.0;
    ret+=exp(-length(v.xz))*(1.0-pow(0.5+0.5*cos(K*v.x),16.0))*(1.0-pow(0.5+0.5*cos(K*v.z),16.0));
    float r=0.0;
    for(int i=0;i<5;++i)
    {
        r+=sin(xy.x)*sin(xy.y);
        xy=mat2(1.1,0.5,-0.5,1.1)*xy+2.0*r*r;
    }
    ret+=vec3(0.25,0.5,0.125)+0.0625*vec3(-0.5,+1.0,0.0)*r*exp(-0.02*length(v-ro));
    return ret;
}

// Ball lens.
// See https://en.wikipedia.org/wiki/Lens#Lensmaker's_equation.
float focal_length(float R,float n)
{
    return n*R/(2.0*(n-1.0));
}

float ray_sphere(vec3 ro,vec3 rd,vec3 c,float R)
{
    const float inf=uintBitsToFloat(0x7F800000u);
    float p=dot(ro-c,rd),q=dot(ro-c,ro-c)-R*R,d=p*p-q;
    if(d<0.0) return -inf;
    float tm,tp;
    if(p<0.0) {tm=q/(+sqrt(d)-p);tp=-p+sqrt(d);}
    else {tm=-p-sqrt(d);tp=q/(-p-sqrt(d));};
    return tm<0.0?(tp<0.0?-inf:tp):tm;
}

vec3 cast_ray_bg(vec3 ro,vec3 rd)
{
    if(rd.y>=0.0) return sky(ro,rd);
    else return ground(ro,rd);
}

vec3 cast_ray(vec3 ro,vec3 rd)
{
    int count=4;
    bool air=true;
    vec3 ret=vec3(0.0);
    float w=1.0;
    for(int i=0;i<count;++i)
    {
        float tA=ray_sphere(ro,rd,A.xyz,A.w);
        float tB=ray_sphere(ro,rd,B.xyz,B.w);
        float tC=ray_sphere(ro,rd,C.xyz,C.w);
        vec3 c=A.xyz;
        float t=tA;
        if(t<-1e-10||tB>1e-10&&tB<t) {t=tB;c=B.xyz;}
        if(t<-1e-10||tC>1e-10&&tC<t) {t=tC;c=C.xyz;}
        if(t<0.0) break;
        ro=ro+t*rd;
        vec3 normal=normalize(ro-c);
        if(!air) normal=-normal;
        // Since refract() doesn't do total internal reflection,
        // we roll our own.
        //float eta=(air?1.0/n:n);
        float eta=(air?1.0/n:n);
        float k=1.0-eta*eta*(1.0-dot(normal,rd)*dot(normal,rd));
        if(k<0.0) rd=reflect(rd,normal);
        else
        {
            // Only use BG for reflections, and only from air.
            if(air)
            {
                // Schlick's approximation.
                float r0=pow((n-1.0)/(n+1.0),2.0);
                float r=r0+(1.0-r0)*pow(clamp(1.0-abs(dot(normal,rd)),0.0,1.0),5.0);
                ret+=w*r*cast_ray_bg(ro,reflect(rd,normal));
                w*=1.0-r;
            }
            rd=eta*rd-(eta*dot(normal,rd)+sqrt(k))*normal;
            air=!air;
        }
        ro=ro+0.001*rd;
    }
    ret+=w*cast_ray_bg(ro,rd);
    return ret;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    float rA=1.0,rB=1.0,rC=5.0;
    float fA=focal_length(rA,n),fB=focal_length(rB,n),fC=focal_length(rC,n);
    float h=1.25*fB;
    float f=1.0/(1.0/fB-1.0/h);
    float d=0.875*fA;
