//kind of breaks down for points with very high curvature and/or very thick lines
float F(float x)
{
    x -= iResolution.x / 2.0f;
    return x * sin(x / (iResolution.x / 40.0f)) / 2.0f + iResolution.y / 2.0f;
}

float GradF(float x)
{
    float eps = 1.0f; //in pixels
    return (F(x + eps) - F(x - eps)) / (2.0f * eps);
}

float CosAng(float x)
{
    float tanAng = GradF(x);
    float cosAng = sqrt(1.0f / (tanAng * tanAng + 1.0f));
    return cosAng;
}

float DistF(vec2 xy)
{
    return abs(F(xy.x) - xy.y) * CosAng(xy.x);
}

float AALine(float dist, float width)
{
    return 1.0f - clamp((dist - width), 0.0f, 1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width = iResolution.x / 1000.0f * (sin(iTime * 3.0f) + 1.0f);

    float separator = iMouse.z > 0.5f ? iMouse.x : ((sin(iTime) * 0.5f + 0.5f) * iResolution.x);
    fragColor = vec4(0.03f);
    if(fragCoord.x < separator)
    {
        fragColor.rgb = mix(fragColor.rgb, vec3(1.0f), AALine(DistF(fragCoord.xy), width));
    }else
    {
        fragColor.rgb = mix(fragColor.rgb, vec3(1.0f), AALine(abs(F(fragCoord.x) - fragCoord.y), width));
    }
    fragColor = mix(fragColor, vec4(0.0f, 1.0f, 0.0f, 1.0f), AALine(abs(fragCoord.x - separator), 0.5f));
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0f / 2.2f));
}

