const float GRID_SIZE = 12.;

const vec3 COLOR_ACCENT_1 = vec3(0, 0, 0);
const vec3 COLOR_ACCENT_2 = vec3(.01, .2, .37);
const vec3 COLOR_DARK = vec3(.11, .08, .5);

float hash2(vec2 p) {
	return texture(iChannel0, p).x;
}

vec3 lerpColor(vec3 from, vec3 to, float progress) {
    vec3 range = to - from;
    return from + range * progress;
}

vec4 make_grid(vec2 uv) {
    vec2 coord = floor(uv * GRID_SIZE);
    vec2 cell_uv = fract(uv * GRID_SIZE);
    return vec4(coord, cell_uv);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    float aa = 8. / iResolution.y;
    vec4 grid = make_grid(uv - vec2(0., iTime * .1));

    // layer moving noise in four directions
    float noise = 0.;
    noise += texture(iChannel0, (uv - 13.) / 16. + iTime * .007).x;
    noise += texture(iChannel0, (uv + 167.) / 16. - iTime * .01).x;
    noise += texture(iChannel0, (uv + 31.) / 16. * vec2(-1., 1.) - iTime * .005).x;
    noise += texture(iChannel0, (uv - 71.) / 16. * vec2(1., -1.) - iTime * .013).x;
    noise /= 4.;

    float h = hash2(grid.xy / 100.);
    vec2 st = grid.zw;

    // flip into quadrants
    if (int(grid.x) % 2 == 0) {
        st.x = 1. - st.x;
    }
    if (int(grid.y) % 2 == 0) {
        st.x = 1. - st.x;
    }

    // flip tile randomly
    if (h > .5) {
        st.x = 1. - st.x;
    }

    // mirror over x = -y + 1
    st = st.x + st.y < 1.
        ? vec2(st.x, st.y)
        : vec2(1. - st.y, 1. - st.x);

    // truchet distance
    float d = h > .5 ? length(st) : 1. - length(st);

    float y = uv.y + .5;
    // adjust the slope of the gradient so it isn't as overpowering
    float y_fix = 1. - pow(1. - y, 2.);
    float width = noise * .4 + .3;
    vec3 color = lerpColor(
        lerpColor(COLOR_DARK, COLOR_ACCENT_1, y),
        lerpColor(COLOR_ACCENT_2, COLOR_DARK, y_fix),
        smoothstep(width + aa, width - aa, d)
    );

    vec3 srgb = pow(color, vec3(1. / 2.2));
    fragColor = vec4(srgb, 1.0);
}

