void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;
    float t = iTime*1.+m.x+(m.y*4.);
    vec2 offset = vec2(sin(t)*0.006,cos(t)*0.006);
    vec3 col = vec3(0.);

    uv += iTime*0.02;  // slow diagonal move
	float detail = 5.; // 1-6 working range

    // Spin that shit ( a little bit )
    float si = sin(t*.02);
    float co = cos(t*.02);
    mat2 rot = mat2(co,-si,si,co);
    uv *= rot;

    //if (mod(iTime,3.)<.5) detail -= 2.;

    float c = GetClouds(uv,t,detail);
    float edge = GetClouds(uv+offset,t,detail);

    //calc HSV color (taken from https://www.shadertoy.com/view/XdyXz3)
    float h = c*.9 - .2; //rainbow hue
    float s = 0.5;
    float v = 0.9 - ( c*(detail*.13) ); //deeper is darker

