vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv);

    vec3 col = palette(d + iTime);

    d = sin(d * 8. - iTime) / 8.;
    d = abs(d);

    d = 0.02 / d;

    col *= d;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    const float lineWidth = 0.01;

    vec3 col = vec3(smoothstep(2.0 / iResolution.y, 0.0, abs(uv.x - uv.y) - lineWidth));

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y * 2.;

    vec3 col = vec3(0.);
    float index = 0.;

    uv = fract(uv) * 2.;
    index += step(1., uv.y) * 2.;
    index += step(1., uv.x);

    float p = mod(iTime, 2.);
    if (index == 0.) {
      col = vec3(step(p, uv.y));
    } else if (index == 3.) {
      col = vec3(step(p, 1. - fract(uv.y)));
    } else if (index == 2.) {
      col = 1. - vec3(step(p, uv.x));
    } else {
      col = 1. - vec3(step(p, 1. - fract(uv.x)));
    }

    if (mod(iTime, 4.) > 2.) {
        col = 1. - col;
    }

    fragColor = vec4(col,1.0);
}

// Decodering
// Leon Denise 2023-10-29
// inspired by Ryoiji Ikeda and Evangelion

// 2023-10-31: updated code from Fabrice Neyret suggestions

// Blackle Mori
// https://suricrasia.online/blog/shader-functions/
#define erot(p,A,a) mix(dot(A, p)*A, p, cos(a)) + cross(A,p)*sin(a)

// Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 color, in vec2 pixel )
{
    color = vec4(0,0,0,1);

    // coordinates
    vec2 p = (pixel-iResolution.xy/2.)/iResolution.y;

    // perspective
    vec3 q = vec3(p, 1.);
    q = erot(q, vec3(1,1,1), -.5);
    p = q.xy/q.z;

    // scroll
    p.y += iTime*.1;

    // grid
    float grid = 16.;
    vec2 cell = floor(p*grid);

    // column animation
    float mask = floor(iTime*hash11(cell.x));
    mask = step(.5,sin(mask));
    float speed = 40.*hash11(cell.x+75.);
    cell.y += floor(iTime*speed)*mask;

    // random per cell
    vec2 rng = hash22(cell);

    // character selection
    int char = int(iTime * rng.x);
    int grd = int(grid);
    char = (char % 50)+145;
    vec2 offset = vec2(char%grd, char/grd);

    // atlas coordinates
    p = mod(p, 1./grid);
    p += offset/grid;
    vec4 map = textureLod(iChannel0, p, 1.5/q.z);

    // color
    bool colorful = sin(rng.y*6.+iTime) > 0.5;
    if (colorful)
    {
        color.rgb = .5+.5*cos(vec3(0,2,4)+floor(cell.y*.1));
        color.rgb *= 1.-map.r;
    }
    else
    {
        color.rgb = vec3(map.r);
    }
}

