vec3 TrackPos (float z)
{
  return vec3 (dot (trkAx, sin (trkFx * z)), dot (trkAy, sin (trkFy * z)), z);
}

vec3 TrackVel (float z)
{
  return normalize (vec3 (dot (trkAx, trkFx * cos (trkFx * z)),
     dot (trkAy, trkFy * cos (trkFy * z)), 1.));
}

float TSurfHt (vec2 tc)
{ // heightmap constructed in Buff A
  return LoadTx (tc * txAsp).x;
}

float RdTun (vec3 p)
{
  float a, rh;
  a = 0.5 * (atan (- p.x, p.y) / pi + 1.);
  rh = TSurfHt (vec2 ((p.z - txOrg) / (2. * pi * tunRad), a));
  return tunRad + dot (trkAw, sin (trkFw * p.z)) -
     rh * (1. - 0.5 * smoothstep (0.5, 1., p.y / tunRad));
}

float TunDf (vec3 p)
{
  vec3 q;
  vec2 rt;
  float dMin, d;
  dMin = dstFar;
  p.xy -= TrackPos (p.z).xy;
  d = RdTun (p) - length (p.xy);
  DMIN (idWal);
  d = (0.82 - 0.02 * p.x * p.x) * tunRad + p.y;
  DMIN (idFlr);
  return 0.5 * dMin;
}

float TunRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = TunDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 TunNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 0.1 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TunDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float VbalDf (vec3 p, float dMin)
{
  vec3 q, qn, uf;
  float d, rad, len, h, sbFac;
  sbFac = 1.65;
  dMin /= 0.6 * sbFac;
  q = p / sbFac;
  rad = 1.;
  qn = normalize (q);
  uf = SphFib (qn, 120.);
  d = PrSphDf (q, rad * (1. - 0.05 * smoothstep (0.1, 0.2, length (qn - uf))));
  DMIN (idVbal);
  q = RotToDir (uf, vec3 (0., 0., 1.), q - uf);
  h = Hashfv3 (41. * uf);
  len = 0.3 * (1. - 0.2 * h);
  q.z -= len;
  d = PrCylDf (q, 0.05 * (1. - 0.1 * q.z / len), len);
  if (d < dMin) qSpk = h;
  DMIN (idVspk);
  q.z -= len + 0.05;
  q.xy = Rot2D (q.xy, pi * (h / 3. + 0.2 * tCur * sign (h - 0.5)));
  q.xy = Rot2D (q.xy, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 0.08;
  d = PrSphDf (q * vec3 (1., 1., 1.3), 0.1);
  DMIN (idVcap);
  return 0.6 * sbFac * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d;
  dMin = dstFar;
  qq = p;
  qq.xy -= TrackPos (qq.z).xy;
  q = qq;
  d = length (max (abs (vec2 (abs (q.x) - 0.7, q.y + 0.82 * tunRad)) -
      vec2 (0.06, 0.25), 0.));
  DMIN (idRail);
  q.y -= -0.82 * tunRad - 0.05;
  q.z = mod (q.z + 1., 2.) - 1.;
  d = PrBoxDf (q, vec3 (0.9, 0.2, 0.1));
  DMIN (idTie);
  q = qq;
  q.z = mod (q.z + lmpSep, 2. * lmpSep) - lmpSep;
  d = 0.8 * PrTorus4Df (q, 0.16, tunRad - 0.18) - 0.15 *
     (0.5 + 0.5 * sqrt (abs (sin (15. * atan (q.y, q.x)))));
  DMIN (idArch);
  q.y -= tunRad - 0.4;
  d = PrSphDf (q, 0.3);
  DMIN (idLmp);
  q = p - vbalPos;
  d = PrSphDf (q, tunRad - 0.9);
  if (d < 0.1) {
    q.yz = Rot2D (q.yz, vbalSpn);
    q.xz = Rot2D (q.xz, vbalDir);
    dMin = VbalDf (q, dMin);
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 0.001 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec3 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  e = vec2 (0.002, 0.);
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vn;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col, q, u;
  vec2 vf;
  int idObjT;
  float dstObj, dstTun, dFac, s, att;
  col = vec3 (0.);
  dstTun = TunRay (ro, rd);
  idObjT = idObj;
  dstObj = ObjRay (ro, rd);
  if (min (dstTun, dstObj) < dstFar) {
    if (dstTun < dstObj) {
      dstObj = dstTun;
      idObj = idObjT;
    }
    ro += dstObj * rd;
    if (dstObj == dstTun) {
      vn = TunNf (ro);
      dFac = (1. - smoothstep (0.2, 0.5, dstObj / dstFar));
      q.xy = TrackPos (ro.z).xy;
      ro.xy -= q.xy;
      if (idObj == idWal) {
        u = vec3 (2. * pi * tunRad * (atan (ro.y, - ro.x) / (2. * pi) + 0.5), 4. * length (ro.xy), ro.z);
        vn = VaryNf (4. * u, vn, 4. * dFac * dFac * (1. - 0.5 * smoothstep (0.7, 1., - vn.y)));
        s = Fbm3 (4. * u);
        col4 = vec4 (mix (vec3 (0.4, 0.4, 0.45), vec3 (0.45, 0.4, 0.35),
           smoothstep (0.3, 0.6, s)), 0.);
      } else if (idObj == idFlr) {
        s = dFac * (1. - smoothstep (-0.2, -0.1, dot (rd, vn)));
        vn = RippleNorm (ro.zx, vn, 6. * s);
        vn = VaryNf (16. * ro, vn, s);
        col4 = vec4 (vec3 (0.5, 0.35, 0.2) * (1. - 0.2 * smoothstep (0.4, 0.85,
           length (vec2 (ro.x, mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep)) / tunRad)), 0.);
      }
      ro.xy += q.xy;
    } else {
      q = ro - vbalPos;
      vn = ObjNf (ro);
      if (idObj >= idVbal) {
        q.yz = Rot2D (q.yz, vbalSpn);
        q.xz = Rot2D (q.xz, vbalDir);
      }
      if (idObj == idLmp) {
        col4 = vec4 (vec3 (1., 1., 0.7) * 0.5 * (1. - vn.y), -1.);
      } else if (idObj == idRail) {
        col4 = vec4 (0.6, 0.6, 0.65, 0.3);
      } else if (idObj == idTie) {
        col4 = vec4 (0.4, 0.3, 0., 0.);
      } else if (idObj == idArch) {
        col4 = vec4 (0.3, 0.3, 0.3, 0.05);
        vn = VaryNf (32. * ro, vn, 2.);
      } else if (idObj == idVbal) {
        col4 = vec4 (0.5, 0.5, 0.1, 0.1);
        vf = vec2 (8., 8.);
      } else if (idObj == idVspk) {
        col4 = vec4 (HsvToRgb (vec3 (qSpk, 0.7, 0.7)), 0.2);
        vf = vec2 (64., 0.1);
      } else if (idObj == idVcap) {
        col4 = vec4 (HsvToRgb (vec3 (qSpk, 1., 1.)), 0.3);
        vf = vec2 (64., 2.);
      }
      if (idObj >= idVbal) {
        if (idObj == idVbal) col4.rgb = mix (col4.rgb, 1.5 * vec3 (0.2, 1., 0.2),
           SmoothBump (0.45, 0.55, 0.005, mod (0.7 * tCur, 1.)));
        else col4.rgb = mix (col4.rgb, 1.5 * vec3 (1., 1., 0.9),
           SmoothBump (0.49, 0.51, 0.005, mod (0.5 * length (q) - 0.7 * tCur, 1.)));
        vn.yz = Rot2D (vn.yz, vbalSpn);
        vn.xz = Rot2D (vn.xz, vbalDir);
        vn = VaryNf (vf.x * q, vn, vf.y);
        vn.xz = Rot2D (vn.xz, - vbalDir);
        vn.yz = Rot2D (vn.yz, - vbalSpn);
      }
      if (col4.a >= 0.) col4.rgb *= (1. - 0.4 * smoothstep (0.4, 0.85,
         abs (mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep) / tunRad));
      else col = col4.rgb;
    }
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, ltDir), 0.) +
       col4.a * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.));
    if (idObj == idFlr || idObj == idRail || idObj == idTie || idObj == idWal) {
      col *= 1. + 0.6 * vec3 (0.3, 1., 0.3) * SmoothBump (-0.01, 0.01, 0.005,
         abs (fract (0.01 * (ro.z - 20. * tCur)) - 0.5) - 0.25);
      if (idObj == idWal) col += 1.5 * vec3 (1., 1., 0.5) *
         pow (max (dot (reflect (rd, vn), ltDir), 0.), 256.);
      else col *= 0.8 + 0.2 * smoothstep (0., 0.5, length (ro.xz - vbalPos.xz) - 2.);
    }
    att = SmoothMin (350. / (dstObj * dstObj), 1., 0.5) * (1. - smoothstep (60., 70., dstObj));
    col *= att;
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 ro, rd, vd;
  vec2 uv;
  float el, az, tunPos, zmFac, t;
  nFrame = iFrame;
  uv = 2. * fragCoord.xy / iResolution.xy - 1.;
  uv.x *= iResolution.x / iResolution.y;
  stDat = Loadv4 (vec2 (0., 0.));
  tunPos = stDat.y;
  txOrg = stDat.z;
  txAsp = stDat.w;
  stDat = Loadv4 (vec2 (1., 0.));
  tunRad = stDat.y;
  az = stDat.z;
  el = stDat.w;
  stDat = Loadv4 (vec2 (2., 0.));
  tCur = stDat.y;
  trkFx = 2. * pi * vec2 (0.005, 0.0087);
  trkAx = tunRad * vec2 (2.2, 1.7);
  trkFy = 2. * pi * vec2 (0.017, 0.0227);
  trkAy = tunRad * vec2 (0.13, 0.07);
  trkFw = 2. * pi * vec2 (0.05, 0.028);
  trkAw = tunRad * vec2 (0.07, 0.04);
  vd = TrackVel (tunPos);
  az += 0.5 * pi + atan (- vd.z, vd.x);
  el += asin (vd.y) - 0.02 * pi;
  zmFac = 3.5 - 0.5 * SmoothBump (0.3, 0.7, 0.1, abs (az) / pi);
  t = tunPos + tunRad * sign (0.5 * pi - abs (az)) * (4. + clamp (6. * (Fbm1 (0.02 * tCur) - 0.5),
     -2.5, 2.5));
  vbalPos = TrackPos (t);
  vd = TrackVel (t);
  vbalDir = -0.5 * pi - atan (vd.z, - vd.x);
  vbalSpn = -0.5 * pi * tCur;
  vuMat = StdVuMat (el, az);
  ro = TrackPos (tunPos) + vuMat * vec3 (0., 0.1, - 0.5) * tunRad;
  dstFar = 100.;
  lmpSep = 10.;
  ltDir = vuMat * normalize (vec3 (0., 0.5, -1.));
  rd = vuMat * normalize (vec3 (uv , zmFac));
  rd.zx = Rot2D (rd.zx, 0.1 * atan (ro.x));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrTorus4Df (vec3 p, float ri, float rc)
{
  vec2 q;
  q = vec2 (length (p.xy) - rc, p.z);
  q *= q;
  return sqrt (sqrt (dot (q * q, vec2 (1.)))) - ri;
}

vec3 SphFib (vec3 v, float n)
{   // Keinert et al's inverse spherical Fibonacci mapping
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, z, ni;
  const float phi = 1.618034;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = VAR_ZERO; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
    }
  }
  return vfMin;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec3 RotToDir (vec3 v1, vec3 v2, vec3 p)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = dot (v1, v2);
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (mod (dot (p, vec3 (37., 39., 41.)), 2. * pi)) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (mod (p + vec2 (0., 1.), 2. * pi)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (mod (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y),
     2. * pi)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 LoadTx (vec2 uv)
{
  return texture (txBuf, uv);
}

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

