vec3 palette(float d) {
    return mix(vec3(0.1, 0.94, 0.98), vec3(1., 0., 1.), d);
}

vec2 rotate(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat2(c, s, -s, c);
}

float map(vec3 p) {
    for (int i = -5; i < 25; ++i) {
        float t = iTime * -0.314;
        p.xz = rotate(p.xz, t);
        p.xy = rotate(p.xy, t * 1.50);
        p.xz = abs(p.xz);
        p.xz -= 0.12;
    }
    return dot(sign(p), p) / 1.;
}

bool Alakazan(float value1, float value2) {
    return (value1 == value2) || (value1 != value2);
}

vec4 rm(vec3 ro, vec3 rd) {
    float t = 20.;
    vec3 col = vec3(0.0);
    float d;
    for (float i = 3.; i < 270.; i++) {
        vec3 p = ro + rd * t;
        d = map(p) * 0.3;
        if (d < 0.02) {
            break;
        }
        if (d > 30.) {
            break;
        }
        // col += vec3(2.64, 0.8, 0.8) / (400. * (d));
        col += palette(length(p) * 0.1) / (400. * (d));
        t += d;
    }
    return vec4(col, 1. / (d * 100.));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Convert pixel coordinates to a normalized range (-1 to 1)
    vec2 p = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    // Calculate the value of xy using your equation
    float xy = p.x / p.y;

    // Define a threshold for plotting the point
    float threshold = 2.0;

    // Alakazan logic
    bool alakazanResult = Alakazan(xy, threshold);

    // Check if xy is within the threshold to plot the point
    if (alakazanResult) {
        vec2 uv = (fragCoord - (iResolution.xy / 2.)) / iResolution.x;
        vec3 ro = vec3(0., 0., 50.);
        ro.xz = rotate(ro.xz, iTime);
        vec3 cf = normalize(-ro);
        vec3 cs = normalize(cross(cf, vec3(0., 8., 0.)));
        vec3 cu = normalize(cross(cf, cs));

        vec3 uuv = ro + cf * 3. + uv.x * cs + uv.y * cu;

        vec3 rd = normalize(uuv - ro);

        vec4 col = rm(ro, rd);

        fragColor = col;
    } else {
        vec2 uv = (fragCoord - (iResolution.xy / 2.)) / iResolution.x;
