vec2 mainSound(int samp, float time) {
    Song song = getSong(time, REVERB_SAMPLES);
    vec2 dry = (song.leads + song.bass) * song.sidechain + song.drums;
    vec2 wet = song.reverb * song.sidechain;
    return mix(dry, wet, WETNESS) * 0.45;
}

