// Generic outlines by computing second SDF at the closest point to first SDF.
// Not perfect as it uses numerical derivatives to compute the closest point,
// leading to floating-point precision errors, but pretty close!
//
// Outline shape switches between rounded box and star shape
//
// Next thing to try perhaps would be to use analytic normals. All SDF functions
// made by iq (except the outlining function)

float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float dot2( in vec2 v ) { return dot(v,v); }
float cro( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

// signed distance to a quadratic bezier
float sdBezier( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;

    float kk = 1.0/dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b))/3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;
    float sgn = 0.0;

    float p  = ky - kx*kx;
    float q  = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float p3 = p*p*p;
    float q2 = q*q;
    float h  = q2 + 4.0*p3;

    if( h>=0.0 ) 
    {   // 1 root
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.0;

        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = clamp( uv.x+uv.y-kx, 0.0, 1.0 );
        vec2  q = d+(c+b*t)*t;
        res = dot2(q);
    	sgn = cro(c+2.0*b*t,q);
    }
    else 
    {   // 3 roots
        float z = sqrt(-p);
        float v = acos(q/(p*z*2.0))/3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp( vec3(m+m,-n-m,n-m)*z-kx, 0.0, 1.0 );
        vec2  qx=d+(c+b*t.x)*t.x; float dx=dot2(qx), sx = cro(c+2.0*b*t.x,qx);
        vec2  qy=d+(c+b*t.y)*t.y; float dy=dot2(qy), sy = cro(c+2.0*b*t.y,qy);
        if( dx<dy ) { res=dx; sgn=sx; } else {res=dy; sgn=sy; }
    }

    return sqrt( res );
}

float sdStar( in vec2 p, in float r, in int n, in float m)
{
    // next 4 lines can be precomputed for a given shape
    float an = 3.141593/float(n);
    float en = 3.141593/m;  // m is between 2 and n
    vec2  acs = vec2(cos(an),sin(an));
    vec2  ecs = vec2(cos(en),sin(en)); // ecs=vec2(0,1) for regular polygon

    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));
    p -= r*acs;
    p += ecs*clamp( -dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}

float baseMap(vec2 uv)
{
    //return sdSegment(uv, vec2(-0.5, 0.2), vec2(0.5, 0));
	vec2 v0 = vec2(1.3,0.9)*cos(iTime*0.5 + vec2(0.0,5.0) );
    vec2 v1 = vec2(1.3,0.9)*cos(iTime*0.6 + vec2(3.0,4.0) );
    vec2 v2 = vec2(1.3,0.9)*cos(iTime*0.7 + vec2(2.0,0.0) );
    return sdBezier(uv, v0, v1, v2);
}

mat2 rot(float r){ return  mat2(cos(r), -sin(r), sin(r), cos(r)); }


// cursed macros to simulate functions as first-class types
// pretty useful, I'll have to put these to use sometime else
#define derivative(uv, eps, func) (vec2(func(uv + vec2(eps, 0)) - func(uv - vec2(eps, 0)), func(uv + vec2(0, eps)) - func(uv - vec2(0, eps)))/eps*0.5)
#define closestPoint(uv, eps, func) (uv - derivative(uv, eps, func)*func(uv))

float outlineMap(vec2 uv)
{
    if(int(iTime)/6 % 2 == 0)
        return sdRoundedBox(uv, vec2(0.1), vec4(tanh(sin(iTime*3.1415926535)*3.0)*0.1));
    return sdStar(uv, 0.1, 6, 2.0 + tanh(sin(iTime*3.1415926535)*3.0)*0.5);
}

float map(vec2 uv)
{
    vec2 cp = closestPoint(uv, 0.0001, baseMap);
    return outlineMap(uv-cp);
}

vec3 colorSdf(vec2 uv, vec4 m, float px)
{
    #define PERIOD 200.
    #define STR 0.2
    #define STROKE_WIDTH 0.005
    #define MOUSE_WIDTH 0.005
    float d = map(uv);
    float md = abs(map(m.xy));
    vec3 col = mix(vec3(1.000,0.686,0.035), vec3(0.133,0.459,1.000), sign(d)*0.5+0.5);
    col *= smoothstep(-px*PERIOD, px*PERIOD, cos(d*PERIOD))*STR+1.-STR;
    col = mix(col, vec3(0), smoothstep(STROKE_WIDTH+px, STROKE_WIDTH-px, abs(d)));
    if(m.z > 0.)
    {
        float mdc = smoothstep(px, -px, min(abs(
                distance(m.xy, uv)-md+MOUSE_WIDTH)-MOUSE_WIDTH,
                distance(m.xy, uv)-MOUSE_WIDTH*2.));
        col = mix(col, vec3(0), mdc);
    }
    col /= d+1.;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec4 m = vec4((iMouse.xy*2.-iResolution.xy)/iResolution.y, iMouse.zw);
    float px = 1.5/iResolution.y;

    fragColor = vec4(colorSdf(uv, m, px),1.0);
}

// DISPLAY

float mirroredSmooth(float x, float c, float w) {
    return clamp(smoothstep(1.0, 0.0, abs(x - c) / w), 0.0, 1.0);
}

vec3 colorFromValue(int value) {
  float factor = float(value) / float(totalCategories);
  const float center = 0.5;
