  return vec3(
      mirroredSmooth(factor, center - spread, width),
      mirroredSmooth(factor, center, width),
      mirroredSmooth(factor, center + spread, width)
  );
}

int getValue(ivec2 pos) {
    return int(texelFetch(iChannel0, pos, 0).r);
}

