// 0 - random
// 1 - circle
// 2 - stripes
// 3 - voronoi value
// 4 - voronoi distance
#define START 1

// CELLS
const int cellTimeStep = 1;
const int lossThreshold = -2;

int getCellSize() {
    return int(getVar(iChannel2, VAR_QUALITY));
}

int sampleValue(ivec2 coord) {
    return int(texelFetch(iChannel0, coord, 0).r);
}

int battle(int self, int other) {
  const int center = (totalCategories - 1) / 2;
  int difference = self - center;
  int newOther = umod(other - difference, totalCategories);
  return sign(center - newOther);
}

int battleWithNeighbor(int value, ivec2 coord) {
  return battle(value, sampleValue(coord));
}

int initialValue(ivec2 coord) {
#if START==0
    // noise
    return int(float(totalCategories) * rand(vec2(coord) / iResolution.xy));
#endif
#if START==1
    // circle
    vec2 center = iResolution.xy / float(getCellSize()) / 2.0;
    int dist = int(distance(vec2(coord), center));
    return umod(dist, totalCategories);    
#endif
#if START==2
    // stripes
    return umod((coord.x + coord.y) / 4, totalCategories);
#endif
#if START==3
    // voronoi value
    // vec4(voronoi(4.0 * vec2(fragCoord / iResolution.xy)).y, 0.0, 0.0, 1.0);
    return int(float(totalCategories) * voronoi(24.0 * vec2(coord) / iResolution.xy).y);
#endif
#if START==4
    // voronoi distance
    // vec4(voronoi(4.0 * vec2(fragCoord / iResolution.xy)).y, 0.0, 0.0, 1.0);
    return int(float(totalCategories) * voronoi(12.0 * vec2(coord) / iResolution.xy).x);
#endif
}

bool reset() {
    // R key
    return texelFetch( iChannel1, ivec2(82,0), 0 ).x > 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord) / getCellSize();

    if (iFrame <= 1 || reset()) {
      fragColor = vec4(initialValue(coord), 0.0, 0.0, 1.0);
      return;
    }

    if (umod(iFrame, cellTimeStep) != 0) {
      fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
      return;
    }

    int last = sampleValue(ivec2(fragCoord));
    int score = 0;
    for (int x = -1; x <= 1; x++) {
      for (int y = -1; y <= 1; y++) {
        if (x != 0 || y != 0) {
          score += battleWithNeighbor(last, umod2(coord * getCellSize() + getCellSize() * ivec2(x, y), ivec2(iResolution)));
        }
      }
    }

    int new = last;
    if (score <= lossThreshold) {
      new = umod(new - 1, totalCategories);
    }

    fragColor = vec4(float(new), 0.0, 0.0, 1.0);
}

image_code:
// Core code derived from zenzicubic's https://www.shadertoy.com/view/Ds3SDf

// Functionality to adjust colouring method added along with controls
// to alter the representation and movement. Some combinations result in
// strange effects but I find the given setup to be the most fun.

// Deep into the walk, adjusting the loop break point can result in a sharp
// change due to the nature of the control parameterisation. Just time reset
// if necessary.

// Scale: Wide 5.0 Mid 2.4 Close 1.8

// Parameters
#define NUMITERS 5000
#define PHI 1.61803398874989
#define PI 3.14159265
#define SCALE 5.4

// Control
#define SPLIT true
#define FLIP true

#define ROTATE true
#define DISK true
#define ZOOM true
#define WALK true

// Colouring function, choose 0 to 4
#define COLOUR 0


// Colouring coset parameters
const vec4 count_mixer = vec4(1,2,3,5);
const vec3 coset_div = vec3(2.,3.,5.);



vec2 cinv(vec2 z) {
    return vec2(z.x, -z.y) / dot(z, z);
}
vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}
vec2 cdiv(vec2 a, vec2 b) {
    return cmul(a, cinv(b));
}

vec3 eflip(vec2 p, vec2 ms) {
    // Reflection counters
    int c = 0;
    int d = 0;
    int e = 0;
    int f = 0;
    bool esc;

    for (int i = 0; i < NUMITERS; i++) 
    {
        // Loop break controller
        if (i > int((ms.x))) break;

        esc = true;

        // Reflection generation
        if (length(p) < 1.) {
            p = cinv(p);
            c ++;
            esc = false;
        }
        if ((p.x > 0.) && SPLIT) {
            p.x *= -1.;
            d ++;
            esc = false;
        }
        if (p.x < -.5)
        {
            p.x = -1. - p.x;
            e ++;
            esc = false;
        }
        if ((p.x > .5) && FLIP)
        {
            p.x = 1. - p.x;
            f ++;
            esc = false;
        }


        if (esc) break;
    }


    float t = dot(vec4(c,d,e,f),count_mixer);

    float coset = ms.y*500.;
    vec3 coset_vec = ms.y*coset_div;

    switch (COLOUR) {
        // Coset rgb mixing
        case 0:
            return (0.5+0.5*cos(PI*vec3(float(t))/coset_vec));
        // Scalar coset representation
        case 1:
            return vec3(0.5+0.5*cos(PI*float(t)/coset));
        // Modular arithmetic representation
        case 2:
            return vec3(mod(float(t),coset)/float(coset));
        // Random mixing
        case 3:
            return vec3(0.5/float((c+d)%2),
                        0.4/float((d+e*f)%2),
                        0.6/float((c*f+f-e)%3));
        // Controlled random mixing
        case 4:
            return vec3(0.5*cos(ms.y)/float((c+d)%2),
                        0.4*cos(3.*ms.y-0.2)/float((d+e*f)%2),
                        0.6*cos(2.*ms.y+0.3)/float((c*f+f-e)%3));
    }
}

void mainImage(out vec4 col, in vec2 coord) 
{
    vec2 z = SCALE*(coord - 0.5*iResolution.xy) / iResolution.y;
    vec2 ms = (iMouse.xy-0.5*iResolution.xy) / iResolution.y;

    float t = 0.1*iTime;
    mat2 rot = mat2(1,0,0,1);

    if (ROTATE) {
        rot = mat2(cos(t), sin(t), -sin(t), cos(t));
    }
    if (DISK) {
        z = z*rot;
        z = cdiv(vec2(0., 1.) - vec2(z.y, -z.x), z - vec2(1., 0.));
    }
    if (ROTATE) {
        z.x += cos(iTime);
        z.y += pow(0.4*sin(iTime*PHI), 2.);
    }
    if (WALK) {
        z.x += pow(iTime, 1.);
    }
    if (ZOOM) {
        z.y /= pow(iTime, 2.);
        z.x /= pow(iTime, 2.);
    }
    col = vec4(eflip(z, vec2(exp(7.*(-ms.x+0.5)),exp(7.*(ms.y)))), 0.);
}

/// by Dom Mandy in 2023


void mainImage(out vec4 o, vec2 i) {
    vec2 r = iResolution.xy,
         m = 1.2 * (2. * iMouse.xy - r) / r.y;
    i += i - r;

    // low resolution
    i = round((i + .5) / 8.) * 8.;
    mat2 t, z = 1.2 * mat2(i, -i.y, i) / (r.y - .5);

    // animation
    vec2 n = .618 * vec2(cos(-iTime), sin(-iTime));
    // shift
    n = mix(n, z[0], texelFetch(iChannel1, ivec2(16, 0), 0).r);
    // click
    n = mix(n, m, max(0., sign(iMouse.z)));

    float f, k, e = 9.;
    for (; k++ <= 31.; f < e ? e = f, t = z : t) {
        if (f > 4.) break;

        f = determinant(
            z = z * z + mat2(n, -n.y, n)
        );
    }

    // proportional to distance
    float d = k / 17.,
          // hue angle
          h = atan(t[0].y, t[0].x),
          // ordered bayer
          b = texelFetch(iChannel0, ivec2((i + r) / 8.) % 8, 0).r;
    h = (step(b, .5 + .5 * h / 6.28) - .5) / 2. + round(6. * h / 3.14) * 3.14 / 6.;

    // color mapping
    vec3 v = vec3(h - 2.1, h, h + 2.1);

    o = round(5. * vec4(.5 + .5 * cos(v), 1.)) / 5.;
    o = step(b, d * d) * o;
}


