    vec4 preCol = texelFetch(iChannel0, ivec2(fragCoord), 0);
    float blend = (iFrame < 2) ? 1. : 1./2.; 
    fragColor = mix(preCol, vec4(clamp(col, 0., 1.), t), blend);
    #else
    fragColor = vec4(max(col, 0.), t);
    #endif
}

