mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}

float hash11(float t) {
  return fract( sin(t * 7361.994) * 4518.442);
}




float hash31(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


float closestWall(vec3 p) {
    vec3 d = min(fract(p), 1.0 - fract(p));
    return min(min(d.x, d.y), d.z);
}

float box (vec3 p, vec3 s) {
    vec3 ap = abs(p) - s;
    return length(max(vec3(0.),ap)) + min( 0., max(ap.x,max(ap.y,ap.z)));


}


float tick(float t) {

    float n = floor(t);
    float r = fract(t);
    r = smoothstep(0.,1.,r);
    r = smoothstep(0.,1.,r);
    r = smoothstep(0.,1.,r);

    return n + r;
}
float field (vec3 p, float scale) {


   float tt = tick(iTime*.67);
   scale *= .25;
   p *= scale;
   vec3 id = floor(p)-.5; 
   p = fract(p) -.5;

   float f1 = hash31(id);


   float ball = box(p, pow(f1,sin(tt*1.617)*.5+.5) * vec3(.49));
   // important detail
   float bound = closestWall(p+.5);

   return min(bound + .1, ball)/scale;
}

float map (vec3 p){
    p.xz *= rot(iTime*.1);

    float thing = box(p, vec3(3));

   // geo

    for (float i = 0.; i < 5.; i++) {
       thing = max(thing, -field(p,pow(2.,i)));
    }

    return thing;
}
