    // enjoy!

    // seed: any real number - the random seed that generates the network parameters
    float seed = 7.;

    // layers: integer values only - is the number of hidden layers in the neural network (recommended: 3-10)
    const int layers = 8;

    // density: integer values only - is the number of neurons per hidden layer (recommended: 3-10)
    const int density = 8;

    // wr (weight range): any positive number - the range of possible values of weights from -wr to +wr (experiment!)
    float wr = 3.*sin(iTime);

    // br (bias range): any positive number or zero - the range of possible values of biases from -br to +br (experiment!)
    float br = 1.;

    // smoothness: any real number - the higher the smoothness value, the less discrete the output is (depends on the layers, density, wr, and br) 
    float smoothness = 20.0;

    // uv[0] and uv[1]: any real number - the input weights of the x and y values of the screen respectively (experiment!)
    uv[0] *= 1.0;
    uv[1] *= 1.0;

    // mv[0] and mv[0]: any real number - the input weights of the x and y coordinates of the mouse cursor respectively (recommended: 5.0)
    mv[0] *= 5.0;
    mv[1] *= 5.0;

    // cam[0], cam[1], and cam[2]: any real number - the input weights of the red, green, and blue components from the iChannel0 Webcam respectively (recommended: 1.0)
    cam[0] *= 1.0;
    cam[1] *= 1.0;
    cam[2] *= 1.0;

    // mic[0]: any real number - the input weight from the iChannel1 Microphone (experiment!)
    mic[0] *= 0.0;

    // I'm too lazy to comment consistently on the rest of my code so good luck past this point
    float[layers*density] network;
    float[] inpt = float[](uv[0],uv[1],mv[0],mv[1],cam[0],cam[1],cam[2],mic[0]);
    vec3 col;
    float x;
    float outpt;
    float ep;
    float en;
    for (int h = 0; h < density; h += 1)
    //neural network input loop
    {
        x = rand(seed);
        outpt = x*br;
        seed += x;
        for (int i = 0; i < inputs; i += 1)
        {
            x = rand(seed);
            outpt += inpt[i]*x*wr;
            seed += x;
        }
        ep = exp(outpt);
        en = exp(-1.0*outpt);
        network[h] = (ep-en)/(ep+en);
    }
    //neural network hidden layers loop
    for (int h = 0; h < layers-1; h += 1)
    {
        for (int i = 0; i < density; i += 1)
        {
            x = rand(seed);
            outpt = x*br;
            seed += x; 
            for (int j = 0; j < density; j += 1)
            {
                x = rand(seed);
                outpt += network[h*density+j]*x*wr;
                seed += x;
            }
            ep = exp(outpt);
            en = exp(-1.0*outpt);
            network[(h+1)*density+i] = (ep-en)/(ep+en);
        }
    }
    //neural network output loop
    for (int h = 0; h < outputs; h += 1)
    {
        for (int i = 0; i < density; i += 1)
        {
            x = rand(seed);
            outpt += network[density*(layers-1)+i]*x*wr;
            seed += x;
        }
        ep = exp(outpt/smoothness);
        en = exp(-1.0*outpt/smoothness);
        col[h] = (ep-en)/(ep+en)+1.0/2.0;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

