void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  // nav

  vec3 s = vec3 (0,0,-4);
  s *= (sin(iTime*.3)*.5+.5)+.2;
  vec3 t = vec3(0);

  float tt = iTime * .3;
  s.xz *= rot(tt*.4);
  s.xy *= rot(tt*.5);
  s.zy *= rot(tt*.7);


  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=-normalize(cross(cz,cx));

  vec3 r=normalize(cx*uv.x+cy*uv.y+cz);



  vec3 col = vec3(0);

  vec3 p = s;

  float dd = 0.;

  float side=sign(map(p));

  for(int i = 0; i < 400; i++) {

      float d=map(p)*side;

      if(d<0.001) {
          vec3 n = norm(p)*side;
          vec3 l = normalize(vec3(-1));

          if ( dot(n,l) < 0.) {
              //l = -l;
          }
          vec3 h = normalize(l-r);

          float diff = max(0., dot(n,l));
          float spec =( 0.4 + pow(max(0., dot(n,h)),100.));

          vec3 remaining = vec3(1) - col;
         // col += pow(remaining * diff *  spec, vec3(2.));
          col += remaining  *  spec * .2;

          side = -side;

          d = .1;

      }
      if ( dd > 10.) {
          break;
      }

      dd += d;
      p+=r*d;
  }



  fragColor = vec4(col, 1);
}

#define PI 3.14159265

vec3 palette(float x)
{
    return 0.5 + 0.5 * cos(x + vec3(0,2,4));
}

vec3 graph(vec2 p)
{
    float t = iTime * 0.1;
    vec3 col = vec3(0.0);
    for (float y = -2.0; y <= 1.0; y += 0.05) {
        float i = y + fract(t);
        float dist = abs(p.y - i - cos(p.x * PI) * sin(i * PI) / PI);
        float intensity = exp(dist * -50.0);
        float highlight = exp(dist * -200.0);
        col += palette(y * PI * 2.0 + t) * intensity + highlight;
    }
    return col;
}

