vec3 draw_cube(vec2 uv, float e, vec4 q)
{
    const vec4 p = vec4(1.0, -1.0, 0.0, 1.0);

	vec3 b1 = quatRotate(q, p.yyy);
    vec3 b2 = quatRotate(q, p.yyx);
    vec3 b3 = quatRotate(q, p.xyx);
    vec3 b4 = quatRotate(q, p.xyy);

   	vec3 t1 = quatRotate(q, p.yxy);
    vec3 t2 = quatRotate(q, p.yxx);
    vec3 t3 = quatRotate(q, p.xxx);
    vec3 t4 = quatRotate(q, p.xxy);

    const float zd = 10.0;

  	b1.xy = b1.xy / (b1.z + zd);
    b2.xy = b2.xy / (b2.z + zd);
    b3.xy = b3.xy / (b3.z + zd);
   	b4.xy = b4.xy / (b4.z + zd);

    t1.xy = t1.xy / (t1.z + zd);
    t2.xy = t2.xy / (t2.z + zd);
    t3.xy = t3.xy / (t3.z + zd);
    t4.xy = t4.xy / (t4.z + zd);

    float tp1 = dot(uv - t1.xy, normalize(t2.xy - t1.xy).yx * vec2(-1.0, 1.0));
    float tp2 = dot(uv - t1.xy, normalize(t4.xy - t1.xy).yx * vec2(-1.0, 1.0));
    float tp3 = dot(uv - t3.xy, normalize(t2.xy - t3.xy).yx * vec2(-1.0, 1.0));
    float tp4 = dot(uv - t3.xy, normalize(t4.xy - t3.xy).yx * vec2(-1.0, 1.0));

    float bp1 = dot(uv - b1.xy, normalize(b2.xy - b1.xy).yx * vec2(-1.0, 1.0));
    float bp2 = dot(uv - b1.xy, normalize(b4.xy - b1.xy).yx * vec2(-1.0, 1.0));
    float bp3 = dot(uv - b3.xy, normalize(b2.xy - b3.xy).yx * vec2(-1.0, 1.0));
    float bp4 = dot(uv - b3.xy, normalize(b4.xy - b3.xy).yx * vec2(-1.0, 1.0));

    float sp1 = dot(uv - b1.xy, normalize(t1.xy - b1.xy).yx * vec2(-1.0, 1.0));
    float sp2 = dot(uv - b2.xy, normalize(t2.xy - b2.xy).yx * vec2(-1.0, 1.0));
    float sp3 = dot(uv - b3.xy, normalize(t3.xy - b3.xy).yx * vec2(-1.0, 1.0));
    float sp4 = dot(uv - b4.xy, normalize(t4.xy - b4.xy).yx * vec2(-1.0, 1.0));


    float ty = min(min(-tp1, tp2), min(tp3, -tp4));
    float by = min(min(bp1, -bp2), min(-bp3, bp4));

    float tx = min(min(tp1, -bp1), min(sp1, -sp2));
    float bx = min(min(tp4, -bp4), min(sp3, -sp4));

    float tz = min(min(-tp2, bp2), min(-sp1, sp4));
    float bz = min(min(-tp3, bp3), min(sp2, -sp3));

    return smoothstep(0.0, e*2.0, vec3(max(tx, bx), max(ty, by), max(tz, bz)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float e = 1.0/iResolution.x;
    vec2 uv = (fragCoord - iResolution.xy*0.5)*e;

    vec3 col = vec3(smoothstep(0.0, e*1.2, length(uv) - 0.2));

    float ff = min(1.0, step(3.0, iMouse.x) + step(3.0, iMouse.y));
    vec4 q = mix(load(QUATROT), quatRandom(iTime * 0.3), 1.0 - ff);

    col += draw_cube(uv, e, q);

    fragColor = vec4(col,1.0);
}

