const ivec2 PMOUSE = ivec2(1, 0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    if (any(greaterThan(fragCoord.xy, vec2(2.0, 1.0)))) return;

    fragColor = load(fragCoord);

    if (iTime == 0.0 || iFrame == 0) {
        store(QUATROT, vec4(vec3(0.0), 1.0));
        store(PMOUSE, vec4(0.0));

        return;
    }

    vec2 m = (iMouse.xy - iResolution.xy*0.5)/iResolution.x;

    vec4 pm = load(PMOUSE).xyzw;

    vec4 q = load(QUATROT);

    float e = 1.0/iResolution.x;

    if (any(greaterThan(iMouse.zw, vec2(0.0)))) {
        if (pm.z == 0.0) pm.xy = m.xy;

        vec2 dm = (m.xy - pm.xy)*5.0 * step(length(m), vec2(0.2));

        vec4 invq = quatConjugate(q);
        vec3 rx = quatRotate(invq, vec3(1.0, 0.0, 0.0));
        vec3 ry = quatRotate(invq, vec3(0.0, 1.0, 0.0));

        vec4 qx = quat(rx, dm.y);
        vec4 qy = quat(ry, -dm.x);

        q = quatMult(q, quatMult(qx, qy));

    	store(QUATROT, normalize(q));
        store(PMOUSE, vec4(vec2(m.xy), 1.0, 1.0));
    }
    else if (pm.z != 0.0) {
        store(PMOUSE, vec4(0.0));
    }
}

