// Created by genis sole - 2023
// License Creative Commons Attribution-NonCommercial 4.0 International.


const ivec2 QUATROT = ivec2(0, 0);
#define load(P) texelFetch(iChannel0, ivec2(P), 0)

vec4 quat(vec3 axis, float rads) {
    return vec4(sin(rads*0.5)*axis, cos(rads*0.5)); 
}

vec4 quatMult(vec4 q1, vec4 q2) {
    return vec4(q1.w*q2.xyz + q2.w*q1.xyz + cross(q1.xyz, q2.xyz),
                q1.w*q2.w - dot(q1.xyz, q2.xyz));
}

vec3 quatRotate(vec4 q, vec3 v) {
    return v + 2.0 * cross(q.xyz, cross(q.xyz, v) + q.w * v);
}

vec4 quatConjugate(vec4 q) {
	return vec4(-q.xyz, q.w);    
}

//David Hoskins hash: https://www.shadertoy.com/view/4djSRW 
float hash11(float p)
{
	vec3 p3  = fract(vec3(p) * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z)*2.0 - 1.0;
}

float gnoise(float p) 
{
    float i = floor(p);
	float f = fract(p);

    float a = dot(hash11(i), f);
    float b = dot(hash11(i + 1.0), f - 1.0);

    float u = smoothstep(0.0, 1.0, f);
    return mix(a, b, u);
}

vec4 quatRandom(float t)
{
    vec3 a = vec3(gnoise(t + 2.0) * 3.1, gnoise(t + 6.0) * 3.5, gnoise(t + 4.0) * 3.2);
    float s = clamp((gnoise(t + 10.0) + 0.5), -1.0, 1.0);
    return normalize(vec4(a * s, sqrt(1.0 - s*s))); 
}

