// note: n.xy==0 is undefined for phi, pleae handle in caller code
vec2 vector_to_zup_spherical_coords( vec3 n )
{
	float theta = safe_acos( n.z ); // note: vectors normalized with normalize() are not immune to -1,1 overflow which cause nan in acos
	float phis = calc_angle( n.xy  );
	return vec2( theta, phis );
}

vec3 yup_spherical_coords_to_vector( vec2 theta, vec2 phis ) { return zup_spherical_coords_to_vector( theta, phis ).yzx ; }
vec3 yup_spherical_coords_to_vector( float theta, float phis ) {
return yup_spherical_coords_to_vector( unit_vector2( theta ), unit_vector2( phis ) ); }

