    float v = 0.5*abs(d.z)+0.5;
    return vec3(pow(1.0-v,2.0),1.0-v,v);}


float sun(vec3 lightdir, vec3 rd){// sun with halo
    float d = max(0.0, dot(rd, lightdir));
    return smoothstep(0.998, 0.999, d) + exp(-pow((1.0-d)*500.0, 2.0));} 


vec3 pass(vec3 ro, vec3 rd, result r){
    vec3 lightdir = normalize(vec3(3.0, 2.0, 1.0));
    //vec3 sky_color = mix(vec3(0.2,0.5,0.5),vec3(0,0,1),abs(-rd.z));
    //vec3 sky_color = mix(vec3(0.2,0.5,0.5),vec3(0,0,0.5),sqrt(abs(rd.z)));
    vec3 sky_color = sky(lightdir, rd);
    sky_color += sun(lightdir, rd);
    vec3 color = vec3(1);
    if(r.dist < DIST_MAX){
        color *= r.color_specular.xyz;
        vec3 dst = ro + rd * r.dist;
        vec3 n = normal(dst);
        //color *= (n * 0.5 + 0.5);
        //color *= material(dst);
        vec3 ambient = vec3(0.4);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * r.dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0);
        color *= (ambient * calcAO(dst, n) + brightness);

        if(brightness > 0.0){
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular * r.color_specular.w;}

        vec3 fog_color = sky_color;
        color = mix(fog_color, vec3(color), exp(-pow(r.dist/20.0, 2.0))); // fog
    }else{
        color *= sky_color;
    }return color;}

float grey(vec3 color){return dot(color, vec3(1.0/3.0));}

void scene2(vec3 ro, vec3 rd, inout vec4 fragColor){
    result res = trace(ro, rd, 0.0, DIST_MAX);
    vec3 color = pass(ro, rd, res);
    if(res.dist < DIST_MAX && res.flag){
        //color = vec3(1);
        ro.z = -ro.z;       // reflect camera position
        rd.z = -rd.z;        // reflect view direction
        res = trace(ro, rd, res.dist+0.01, DIST_MAX);
        vec3 reflection = pass(ro, rd, res);
        //color *= reflection; // clear
        //color = mix(color, reflection, 0.5); // muddy (light)
        color *= mix(vec3(1), reflection, 0.5); // muddy (dark)
        //color += reflection; // additive
        //color *= grey(pass(ro, rd, res)); // unrealistic
    }
    color = tanh(color); // roll-off overly bright colors
    fragColor = vec4(color, 1);
    fragColor = sqrt(fragColor);} // approximate gamma


