float Checkers(in vec2 p) {
    // Filter kernel
    vec2 w = fwidth(p) + .001;
    // Box box filter
    vec2 i = 2. * (abs(fract((p - .5 * w) * .5) - .5) - abs(fract((p + .5 * w) * .5) - .5)) / w;
    // xor pattern
    return .5 - .5 * i.x * i.y;
}

// Compute point on ray
vec3 Point(Ray ray, float t) {
    return ray.o + t * ray.d;
}

float hash(float n) {
    return fract(sin(n) * 43758.5453);
}

float noise(vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f * f * (3.0 - 2.0 * f);

    float n = i.x + i.y * 157.0 + i.z * 113.0;
    return mix(mix(mix(hash(n + 0.0), hash(n + 1.0), f.x), mix(hash(n + 157.0), hash(n + 158.0), f.x), f.y), mix(mix(hash(n + 113.0), hash(n + 114.0), f.x), mix(hash(n + 270.0), hash(n + 271.0), f.x), f.y), f.z);
}

float fractalNoise(vec3 p, int octaves, float lacunarity, float persistence) {
    float sum = 0.0;
    float amplitude = 1.0;

    for(int i = 0; i < octaves; i++) {
        sum += noise(p) * amplitude;
        p *= lacunarity;
        amplitude *= persistence;
    }

    return sum;
}

vec3 marbleTexture(vec3 position, float scale, int octaves, float lacunarity, float persistence, vec3 color1, vec3 color2) {
    vec3 p = position * scale;
    float turbulence = fractalNoise(p, octaves, lacunarity, persistence);

    // Adjust the contrast and brightness
    float brightness = 0.5;
    float contrast = 0.5;

    // Interpolate between color1 and color2 based on turbulence
    vec3 marbleColor = mix(color1, color2, turbulence);

    // Apply contrast and brightness
    marbleColor = mix(marbleColor - brightness, marbleColor + brightness, turbulence);

    return marbleColor;
}

// Fonction de texture uniforme
vec3 UniformTexture(vec3 p) {
    return p; // Couleur RGB 
}

// Fonction du Damier Volumique
vec3 CheckerTexture(vec3 position, float checkerSize, vec3 Couleur1, vec3 Couleur2) {
    float checkerValue = Checkers(position.xy / checkerSize);
    return mix(Couleur1, Couleur2, checkerValue);
}

vec3 ConcentricColorTexture(vec3 position, vec3 center, vec3 innerColor, vec3 outerColor) {
    // Calculez la distance entre le point et le centre
    float distance = length(position - center);

    // Normalisez la distance pour obtenir une valeur entre 0 et 1
    float normalizedDistance = distance / max(length(innerColor), length(outerColor));

    // Interpolez entre les couleurs intÃ©rieure et extÃ©rieure en fonction de la distance
    vec3 color = mix(innerColor, outerColor, normalizedDistance);

    return color;
}

