        float checkerValue = Checkers(p.xy / checkerSize);
        if(checkerValue > 0.5) {
            return Material(vec3(1.0, 1.0, 1.0), vec3(0.2, 0.2, 0.2), vec3(0.0, 0.0, .0), vec3(0.0, 0.0, 0.0)); // Diffuse
        } else {
            return Material(vec3(0.0, 0.0, .0), vec3(0.2, 0.2, 0.2), vec3(1.0, 1.0, 1.0), vec3(0.0, 0.0, 0.0)); // SpÃ©culaire
        }
    }
    if(i == 9) {
                //4.0, 10, 3.0, 0.6
        return Material(marbleTexture(p, 6.0, 20, 4.0, 0.6, vec3(0.858, 0.843, 0.698) ,vec3(0.937, 0.917, 0.839)), vec3(0.1, 0.1, 0.1), vec3(0.5, 0.5, 0.5), vec3(0.0, 0.0, 0.0));
    } else if(i == 0) {
        // compute checkboard
        float f = Checkers(.5 * p.xy);
        vec3 col = vec3(.4, .5, .7) + f * vec3(.1);
        return Material(col, vec3(.1, .1, .1), vec3(0.3, 0.3, 0.3), vec3(1.0, 0.0, 0.0));
    }
    return Material(vec3(0), vec3(0), vec3(0), vec3(0));
}

// Sphere intersection
// ray : The ray
//   x : Returned intersection information
bool IntersectSphere(Ray ray, Sphere sph, out Hit x) {
    vec3 oc = ray.o - sph.c;
    float b = dot(oc, ray.d);
    float c = dot(oc, oc) - sph.r * sph.r;
    float d = b * b - c;
    if(d > 0.) {
        float t = -b - sqrt(d);
        if(t > 0.) {
            vec3 p = Point(ray, t);
            x = Hit(t, normalize(p - sph.c), sph.i);

            return true;
        }
    }
    return false;

}

bool IntersectEllipsoid(Ray ray, Ellipsoid ellipsoid, out Hit x) {
    vec3 oc = ray.o - ellipsoid.center;
    vec3 invRadius = 1.0 / ellipsoid.radius;

    vec3 rayDir = ray.d * invRadius;
    vec3 rayOrigin = oc * invRadius;

    float a = dot(rayDir, rayDir);
    float b = 2.0 * dot(rayDir, rayOrigin);
    float c = dot(rayOrigin, rayOrigin) - 1.0;

    float discriminant = b * b - 4.0 * a * c;

    if(discriminant > 0.0) {
        float t0 = (-b - sqrt(discriminant)) / (2.0 * a);
        float t1 = (-b + sqrt(discriminant)) / (2.0 * a);

        float t = min(t0, t1);

        if(t > 0.0) {
            vec3 p = ray.o + ray.d * t;
            vec3 normal = (p - ellipsoid.center) / ellipsoid.radius;

            x = Hit(t, normalize(normal), ellipsoid.i);
            return true;
        }
    }

    return false;
}

bool intersectTorus(Ray ray, Torus torus, out Hit hit) {
    hit.t = -1.0;  // Initialize hit.t to a negative value to indicate no intersection

    vec3 ro = ray.o - torus.center;
    vec3 rd = ray.d;

    float Ra2 = torus.majorRadius * torus.majorRadius;
    float ra2 = torus.minorRadius * torus.minorRadius;

    float m = dot(ro, ro);
    float n = dot(ro, rd);

    float k = (m - ra2 - Ra2) / 2.0;
    float k3 = n;
    float k2 = n * n + Ra2 * rd.z * rd.z + k;
    float k1 = k * n + Ra2 * ro.z * rd.z;
    float k0 = k * k + Ra2 * ro.z * ro.z - Ra2 * ra2;

    float c2 = 2.0 * k2 - 3.0 * k3 * k3;
    float c1 = k3 * (k3 * k3 - k2) + k1;
    float c0 = k3 * (k3 * (-3.0 * k3 * k3 + 4.0 * k2) - 8.0 * k1) + 4.0 * k0;

    c2 /= 3.0;
    c1 *= 2.0;
    c0 /= 3.0;

    float Q = c2 * c2 + c0;
    float R = 3.0 * c0 * c2 - c2 * c2 * c2 - c1 * c1;

    float h = R * R - Q * Q * Q;
    float z = 0.0;

    if(h < 0.0) {
        float sQ = sqrt(Q);
        z = 2.0 * sQ * cos(acos(R / (sQ * Q)) / 3.0);
    } else {
        float sQ = pow(sqrt(h) + abs(R), 1.0 / 3.0);
        z = sign(R) * abs(sQ + Q / sQ);
    }

    z = c2 - z;

    float d1 = z - 3.0 * c2;
    float d2 = z * z - 3.0 * c0;

    if(abs(d1) < 1.0e-4) {
        if(d2 < 0.0) {
            return false;  // No intersection
        }
        d2 = sqrt(d2);
    } else {
        if(d1 < 0.0) {
            return false;  // No intersection
        }
        d1 = sqrt(d1 / 2.0);
        d2 = c1 / d1;
    }

    float result = 1e20;

    h = d1 * d1 - z + d2;

    if(h > 0.0) {
        h = sqrt(h);
        float t1 = -d1 - h - k3;
        float t2 = -d1 + h - k3;

        if(t1 > 0.0) {
            result = t1;
        }
        if(t2 > 0.0) {
            result = min(result, t2);
        }
    }

    h = d1 * d1 - z - d2;

    if(h > 0.0) {
        h = sqrt(h);
        float t1 = d1 - h - k3;
        float t2 = d1 + h - k3;

        if(t1 > 0.0) {
            result = min(result, t1);
        }
        if(t2 > 0.0) {
            result = min(result, t2);
        }
    }

     if (result < 1e20) {
    hit.t = result;  // Update the hit structure with the intersection depth

    // Compute the normal vector
    vec3 q = ray.o + ray.d * hit.t;
    vec3 normal = normalize(vec3(
        2.0 * (q.x - torus.center.x),
        2.0 * (q.y - torus.center.y),
        2.0 * (q.z - torus.center.z)
    ));
    hit.n = normal;

    hit.i = torus.i;  // Assign the color or texture ID to the hit structure
    return true;  // Intersection found
}

    return false;  // No intersection
}

bool IntersectBox(Ray ray, Box box, out Hit x) {
    vec3 tMin = (box.min - ray.o) / ray.d;
    vec3 tMax = (box.max - ray.o) / ray.d;

    vec3 t1 = min(tMin, tMax);
    vec3 t2 = max(tMin, tMax);

    float tNear = max(max(t1.x, t1.y), t1.z);
    float tFar = min(min(t2.x, t2.y), t2.z);

    if(tNear > tFar || tFar < 0.0) {
        return false;
    }

    float tHit = (tNear < 0.0) ? tFar : tNear;

    vec3 hitPoint = Point(ray, tHit);
    vec3 normal = vec3(0.0); // Initialize the normal vector to (0, 0, 0)

    // Calculate normal based on which face of the box was hit
    if(abs(hitPoint.x - box.min.x) < 0.0001) {
        normal = vec3(-1.0, 0.0, 0.0); // Left face
    } else if(abs(hitPoint.x - box.max.x) < 0.0001) {
        normal = vec3(1.0, 0.0, 0.0); // Right face
    } else if(abs(hitPoint.y - box.min.y) < 0.0001) {
        normal = vec3(0.0, -1.0, 0.0); // Bottom face
    } else if(abs(hitPoint.y - box.max.y) < 0.0001) {
        normal = vec3(0.0, 1.0, 0.0); // Top face
    } else if(abs(hitPoint.z - box.min.z) < 0.0001) {
        normal = vec3(0.0, 0.0, -1.0); // Back face
    } else if(abs(hitPoint.z - box.max.z) < 0.0001) {
        normal = vec3(0.0, 0.0, 1.0); // Front face
    }

    x = Hit(tHit, normal, box.i);
    return true;
}

// Plane intersection
// ray : The ray
//   x : Returned intersection information
bool IntersectPlane(Ray ray, Plane pl, out Hit x) {
    float t = -dot(ray.o - pl.p, pl.n) / dot(ray.d, pl.n);
    if(t > 0.) {

        x = Hit(t, vec3(0, 0, 1), pl.i);
        return true;
    }
    return false;
}

bool IntersectCapsule(Ray ray, Capsule capsule, out Hit x) {
    // Initialize the hit to a default value
    x = Hit(1000.0, vec3(0.0), -1);

    // Check for intersection with the first sphere (start point)
    Sphere sphere1 = Sphere(capsule.start, capsule.radius, capsule.i);
    Hit sphereHit1;
    bool hitSphere1 = IntersectSphere(ray, sphere1, sphereHit1);

    // Check for intersection with the second sphere (end point)
    Sphere sphere2 = Sphere(capsule.end, capsule.radius, capsule.i);
    Hit sphereHit2;
    bool hitSphere2 = IntersectSphere(ray, sphere2, sphereHit2);

    // Check for intersection with the cylindrical part (the tube)
    vec3 tubeDir = normalize(capsule.end - capsule.start);
    vec3 oc = ray.o - capsule.start;
    float a = dot(ray.d - dot(ray.d, tubeDir) * tubeDir, ray.d - dot(ray.d, tubeDir) * tubeDir);
    float b = 2.0 * dot(ray.d - dot(ray.d, tubeDir) * tubeDir, oc - dot(oc, tubeDir) * tubeDir);
    float c = dot(oc - dot(oc, tubeDir) * tubeDir, oc - dot(oc, tubeDir) * tubeDir) - capsule.radius * capsule.radius;

    // Calculate the discriminant
    float discriminant = b * b - 4.0 * a * c;

    if(discriminant >= 0.0) {
        float t1 = (-b - sqrt(discriminant)) / (2.0 * a);
        float t2 = (-b + sqrt(discriminant)) / (2.0 * a);

        // Check if t1 and t2 are within the bounds of the cylindrical part
        float h1 = dot(ray.o + t1 * ray.d - capsule.start, tubeDir);
        float h2 = dot(ray.o + t2 * ray.d - capsule.start, tubeDir);

        if(t1 >= 0.0 && h1 >= 0.0 && h1 <= length(capsule.end - capsule.start)) {
            if(t1 < x.t) {
                vec3 hitPoint = Point(ray, t1);
                vec3 normal = normalize(hitPoint - (capsule.start + h1 * tubeDir));
                x = Hit(t1, normal, capsule.i);
            }
        }

        if(t2 >= 0.0 && h2 >= 0.0 && h2 <= length(capsule.end - capsule.start)) {
            if(t2 < x.t) {
                vec3 hitPoint = Point(ray, t2);
                vec3 normal = normalize(hitPoint - (capsule.start + h2 * tubeDir));
                x = Hit(t2, normal, capsule.i);
            }
        }
    }

    // Check if there was an intersection with either of the spheres
    if(hitSphere1 && sphereHit1.t < x.t) {
        x = sphereHit1;
    }

    if(hitSphere2 && sphereHit2.t < x.t) {
        x = sphereHit2;
    }

    return (hitSphere1 || hitSphere2 || x.t < 1000.0);
}

bool IntersectCylinder(Ray ray, Cylinder cyl, out Hit hit) {
    // Initialize the hit to a default value
    hit.t = 1000.0;

    vec3 cylinderAxis = normalize(cyl.end - cyl.start);
    vec3 oc = ray.o - cyl.start;

    float a = dot(ray.d - dot(ray.d, cylinderAxis) * cylinderAxis, ray.d - dot(ray.d, cylinderAxis) * cylinderAxis);
    float b = 2.0 * dot(ray.d - dot(ray.d, cylinderAxis) * cylinderAxis, oc - dot(oc, cylinderAxis) * cylinderAxis);
    float c = dot(oc - dot(oc, cylinderAxis) * cylinderAxis, oc - dot(oc, cylinderAxis) * cylinderAxis) - cyl.radius * cyl.radius;

    float discriminant = b * b - 4.0 * a * c;

    float t1 = (-b - sqrt(discriminant)) / (2.0 * a);
    float t2 = (-b + sqrt(discriminant)) / (2.0 * a);

    float h1 = dot(ray.o + t1 * ray.d - cyl.start, cylinderAxis);
    float h2 = dot(ray.o + t2 * ray.d - cyl.start, cylinderAxis);

    if (discriminant >= 0.0) {
        if (t1 >= 0.0 && h1 >= 0.0 && h1 <= length(cyl.end - cyl.start) && t1 < hit.t) {
            vec3 hitPoint = Point(ray, t1);
            vec3 normal = normalize(hitPoint - (cyl.start + h1 * cylinderAxis));
            hit = Hit(t1, normal, cyl.i);
        }

        if (t2 >= 0.0 && h2 >= 0.0 && h2 <= length(cyl.end - cyl.start) && t2 < hit.t) {
            vec3 hitPoint = Point(ray, t2);
            vec3 normal = normalize(hitPoint - (cyl.start + h2 * cylinderAxis));
            hit = Hit(t2, normal, cyl.i);
        }
    }

    float tTopCap = dot((cyl.start - ray.o), cylinderAxis) / dot(ray.d, cylinderAxis);
    float distanceTopCap = distance(Point(ray, tTopCap), cyl.start);

    if (tTopCap >= 0.0 && distanceTopCap <= cyl.radius && tTopCap < hit.t) {
        hit = Hit(tTopCap, cylinderAxis, cyl.i);
    }

    float tBottomCap = dot((cyl.end - ray.o), -cylinderAxis) / dot(ray.d, -cylinderAxis);
    float distanceBottomCap = distance(Point(ray, tBottomCap), cyl.end);

    if (tBottomCap >= 0.0 && distanceBottomCap <= cyl.radius && tBottomCap < hit.t) {
        hit = Hit(tBottomCap, -cylinderAxis, cyl.i);
    }

    return hit.t < 1000.0;
}


// Scene intersection
// ray : The ray
//   x : Returned intersection information
bool Intersect(Ray ray, out Hit x) {
    // Spheres
    const Sphere sph1 = Sphere(vec3(0., 1., 2.), 1., 3);
    const Sphere sph2 = Sphere(vec3(2., 0., 2.), 1., 3);
    const Plane pl = Plane(vec3(0., 0., 1.), vec3(0., 0., 0.), 8);

    // Ellipsoid
    const Ellipsoid ell = Ellipsoid(vec3(4., -1., 1.), vec3(2., 2., 1.), 7);

    // Torus
    const Torus torus = Torus(vec3(-1.0, -2.0, 2.0), 1.0, 0.3, 5);

    // Box
    const Box box1 = Box(vec3(2.0, 2.0, 1.0), vec3(4.0, 4.0, 3.0), 9);

    const Cylinder cyl = Cylinder(vec3(-3.0, 3.0, 5.0), vec3(-3.0, 3.0, 2.0), 1.0, 4);
    const Capsule cap = Capsule(vec3(-1.0, 3.0, 4.0), vec3(1.0, 3.0, 4.0), 0.5, 2);

    x = Hit(1000., vec3(0), -1);
    Hit current;
    bool ret = false;

    if(IntersectSphere(ray, sph1, current) && current.t < x.t) {
        x = current;
        ret = true;
    }

    if(IntersectPlane(ray, pl, current) && current.t < x.t) {
        x = current;
        ret = true;
    }

    if(IntersectEllipsoid(ray, ell, current) && current.t < x.t) {
        x = current;
        ret = true;
    }

    // Check torus intersection
    if(intersectTorus(ray, torus, current) && current.t < x.t) {
        x = current;
        ret = true;
    }
    if(IntersectCylinder(ray, cyl, current) && current.t < x.t) {
        x = current;
        ret = true;
    }
    // Check box intersection
    if(IntersectBox(ray, box1, current) && current.t < x.t) {
        x = current;
        ret = true;
    }
    if(IntersectCapsule(ray, cap, current) && current.t < x.t) {
        x = current;
        ret = true;
    }

    return ret;
}

vec3 Background(vec3 rd) {
    return mix(vec3(.8, .8, .9), vec3(.7, .7, .8), rd.z);
}

// Camera rotation matrix
// ro : Camera origin
// ta : Target point
mat3 setCamera(in vec3 ro, in vec3 ta) {
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(0, 0, 1);
    vec3 cu = -normalize(cross(cw, cp));
    vec3 cv = -normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

// Apply color model
// m : Material
// n : normal
vec3 Color(Material m, vec3 n, vec3 p, vec3 viewDir) {
    vec3 ambient = m.ambient;
    vec3 diffuse = m.diffuse;
    vec3 specular = m.specular;
    vec3 lightDir = normalize(vec3(1.0, 1.0, 1.0)); // light direction
    Hit temp;

    // Calculate the normal and light direction
    vec3 norm = normalize(n);

    if(!Intersect(Ray(p + norm * 0.01, lightDir), temp)) {
     // Calculate diffuse and specular terms
        float diff = clamp(dot(norm, lightDir), 0., 1.);
        vec3 reflectDir = -lightDir - 2.0 * dot(-lightDir, norm) * norm;
        float spec = max(dot(reflectDir, viewDir), 0.0);

    // Calculate the final color
        vec3 ambientColor = ambient;
        vec3 diffuseColor = diff * diffuse;
        vec3 specularColor = spec * specular;

        return ambientColor + diffuseColor + specularColor;

    } else {

        return vec3(.2, .2, .2);

    }

}

// Hemisphere direction

vec3 Hemisphere(int seed, vec3 n) {

    float a = fract(sin(176.19 * float(seed)));// Uniform randoms

    float b = fract(sin(164.19 * float(seed)));

    float u = 2. * 3.1415 * a;// Random angle

    float v = acos(2. * b - 1.);// Arcosine distribution to compensate for poles

    vec3 d = vec3(cos(u) * cos(v), sin(u) * cos(v), sin(v));// Direction

    if(dot(d, n) < 0.) {
        d = -d;
    }// Hemishpere

    return d;

}

// Ambient occlusion

// p : Point

// n : Normal

// N : Number of samples

float AmbientOcclusion(vec3 p, vec3 n, int N) {
    if(N == 0) {
        return 1.0;  // No occlusion for N=0
    }

    float ao = 0.0;

    for(int i = 0; i < N; i++) {
        vec3 d = Hemisphere(i, n); // d dans la demi-sphere

        // Create a shadow ray from the point to the sample point
        Ray shadowRay;
        shadowRay.o = p + 0.01 * n;
        shadowRay.d = d;

        Hit hit;
        bool hitSomething = Intersect(shadowRay, hit);

        if(hitSomething && hit.t < 1000.) {
            // The sample point is occluded, decrease ambient occlusion
            ao += 1.0;
        }
    }

    // Normalize the accumulated occlusion value to the range [0, 1]
    ao = 1.0 - ao / float(N);

    return ao;
}

vec3 Shade(Ray ray, int maxReflectionLevel, int N) {
    vec3 accumulatedColor = vec3(0.0);

    for(int reflectionLevel = 0; reflectionLevel <= maxReflectionLevel; reflectionLevel++) {
        Hit x;
        bool hitSomething = Intersect(ray, x);

        if(!hitSomething) {
            accumulatedColor += Background(ray.d);
            break;
        }

        vec3 p = Point(ray, x.t);
        Material mat = Texture(p, x.i);
        vec3 norm = normalize(x.n);

        // Calculate the base color (diffuse + ambient + specular)
        vec3 baseColor = Color(mat, x.n, p, normalize(ray.o - p));

        // Calculate ambient occlusion
        float ambientOcclusion = AmbientOcclusion(p, x.n, N);

        // Apply ambient occlusion to the base color
        baseColor *= ambientOcclusion;

        // Handle reflection
        if(reflectionLevel < maxReflectionLevel && (mat.reflectivity.x > 0.0 || mat.reflectivity.y > 0.0 || mat.reflectivity.z > 0.0)) {
            // Calculate the reflection direction
            vec3 reflectDir = normalize(ray.d - 2.0 * dot(ray.d, norm) * norm);

            // Create a reflected ray
            ray.o = p + 0.01 * reflectDir; // Offset origin slightly to avoid self-intersection
            ray.d = reflectDir;

            // Accumulate reflected color
            accumulatedColor += baseColor * mat.reflectivity;
        } else {
            accumulatedColor += baseColor;
            break;
        }
    }

    return accumulatedColor;
}

//Rotation
Ray RotateRay(Ray ray, float angle, vec3 axis) {
    float c = cos(angle);
    float s = sin(angle);
    float t = 1.0 - c;

    vec3 ax = normalize(axis);

    mat3 rotation = mat3(
        t * ax.x * ax.x + c, t * ax.x * ax.y - s * ax.z, t * ax.x * ax.z + s * ax.y,
        t * ax.x * ax.y + s * ax.z, t * ax.y * ax.y + c, t * ax.y * ax.z - s * ax.x,
        t * ax.x * ax.z - s * ax.y, t * ax.y * ax.z + s * ax.x, t * ax.z * ax.z + c
    );

    ray.d = rotation * ray.d;
    return ray;
}

//homothÃ©tie
Ray ScaleRay(Ray ray, vec3 scale) {
    ray.d *= scale;
    return ray;
}

//Transtlation
Ray TranslateRay(Ray ray, vec3 translation) {
    ray.o += translation;
    return ray;
}

