#define R (iResolution.xy)
#define T (iTime)
#define M_PI 3.1415
#define TAU (M_PI*2.0)


float line(vec2 p, vec2 a, vec2 b, float t) {
    vec2 pa = p - a; vec2 ba = b - a;
    return clamp(1.0-length(pa - clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0) * ba)/t, 0.0, 1.0);
}

float hash21(vec2 ip, float seed) {
    uvec2 p = floatBitsToUint(ip);uint s = floatBitsToUint(seed);
    uvec2 k = ~p;uint j = ~s;
    p ^= p << 17U;p ^= p >> 13U;p += p.x * j + p.y;
    p = p + ((p + k) << 3U);p ^= p << 5U;
    return float(p.x + p.y) / float(0xFFFFFFFFU);
}

float noise(vec2 p, float seed) {
    vec2 id = floor(p);
    vec2 lv = fract(p); lv = lv * lv * (3.0 - 2.0 * lv); 
    return mix(
        mix(hash21(id, seed), hash21(id+vec2(1, 0), seed), lv.x),
        mix(hash21(id+vec2(0, 1), seed), hash21(id+vec2(1, 1), seed), lv.x),
        lv.y
    );
}

void mainImage(out vec4 O, in vec2 fc)
{
    vec2 uv = fc.xy / R.y;
    vec3 col = vec3(0.0);
    float cy = 0.3333;
    float v = noise(uv*5.+T, 3.3212)*cy;
    col += vec3(2.2, 0.63, 0.333)*line(uv, vec2(0.0, cy+v), vec2(R.x/R.y, cy+v), 0.01+0.04*noise(uv*40.+T, 1.132));
    col = mix(col, vec3(1, 0, 0), smoothstep(0.0, cy, (v+cy)-uv.y));
    col = pow(col, vec3(1.0/2.2));
    O = vec4(col, 1.0);
}

