           + mat2(1., 0., 0., -1.) * i.y
           // euclidean axis
           + mat2(1., 0., 0., 1.) * cos(iTime)
           // imaginary axis
           + mat2(0., 1., -1., 0.) * sin(iTime);

    // chaotic map
    float f, k, e = 9e9;
    for (; k++ < 9.; f < e ? e = f, t = z[0] : t) {
        f = abs(determinant(
            // the split-quaternion basis is isomorphic to the 2x2 matrices.
            // this means we can use matrix operators like normal.
            z = (z + c) * (z + c) / (z * z - c * c)
        ));
    }

    // map orbit trap
    float h = atan(t.y, t.x) + iTime / 4.,
          d = smoothstep(0., 2., pow(e, -.1));

    // color wheel
    vec3 u = vec3(h - 2.1, h, h + 2.1),
         v = .5 + .5 * cos(u);

    o = vec4(v * d, 1.);
}


