vec2 grad(vec2 uv) {
    // Compute the gradient of the. channel used for shading

    vec4 buffer_up = texture(iChannel0, vec2(uv.x, uv.y + 1.0 / iResolution.y));
    vec4 buffer_down = texture(iChannel0, vec2(uv.x, uv.y - 1.0 / iResolution.y));
    vec4 buffer_left = texture(iChannel0, vec2(uv.x + 1.0 / iResolution.x, uv.y));
    vec4 buffer_right = texture(iChannel0, vec2(uv.x - 1.0 / iResolution.x, uv.y));

    // divide by 4, managable range
    return vec2(buffer_left.y-buffer_right.y, buffer_up.y-buffer_down.y) / 2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 g = grad(uv);
    vec3 t1 = vec3(1,0,20.0*g.x);
    vec3 t2 = vec3(0,1,20.0*g.y);
    vec3 N = normalize(cross(t1,t2));
    vec3 L = normalize(vec3(1,3,5));
    float illum = (0.4+0.4*smoothstep(0.97,0.98, dot(N,L)) + 0.2 * max(0.0, dot(N,L)));

    float substance = 2.0*texture(iChannel0, uv).g;
    // Output to screen
    fragColor = illum*vec4(0.8, 0.8, 0.1, 0.0) + (1.0-substance)*vec4(0.6, 0.4, 0.1, 0.0);

   //fragColor = vec4(1.0)-vec4(1.0,0.8,0.6,0.0)*texture(iChannel0, uv).g;
}

