vec4 lap(vec2 uv) {
    // Compute the Laplacian of all four elements of iChannel0

    vec4 buffer = texture(iChannel0, uv);
    vec4 buffer_up = texture(iChannel0, vec2(uv.x, uv.y + 1.0 / iResolution.y));
    vec4 buffer_down = texture(iChannel0, vec2(uv.x, uv.y - 1.0 / iResolution.y));
    vec4 buffer_left = texture(iChannel0, vec2(uv.x + 1.0 / iResolution.x, uv.y));
    vec4 buffer_right = texture(iChannel0, vec2(uv.x - 1.0 / iResolution.x, uv.y));

    // divide by 4, managable range
    return (buffer_up + buffer_down + buffer_left + buffer_right - 4.0 * buffer) / 4.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 BufferA = texture(iChannel0, uv);

    // Suggested time step
    float dt = 0.5;

    /* Wave simulation constants */
    float d = 0.999;
    float c = 1.4;

    /* Mitosis */
    float Da = 1.0;
    float Db = 0.5;
    float k = 0.06507;
    float f = 0.04938;
    /**/

    float A = texture(iChannel0, uv).r;
    float B = texture(iChannel0, uv).g;

    // INSERT CODE TO UPDATE A and B INSTEAD OF THE LINES BELOW ---> 

    float Ap = A; // velocity
    float Bp = B; // height

    // vec4 delta = d * c * c * lap(uv);
    // Ap += delta.y * dt;
    // Bp += Ap * dt;

    vec4 delta = lap(uv);
    Ap += dt * (Da * delta.x - A*B*B + f*(1.0-A));
    Bp += dt * (Db * delta.y + A*B*B - (k+f)*B);

    // <----------

    vec2 V = fragCoord - iMouse.zw;
    if (iTime < 0.2)
        fragColor = vec4(0, 0, 0, 0);
    else
        fragColor = exp(-0.1*dot(V,V)) * vec4(0,1.0,0,0) + vec4(Ap, Bp, 0.0, 0.0);
}

