#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

float circle(vec2 p, float r) {
  return length(p) - r ;
}

vec3 palette(float a) {
  return 0.5+0.5*sin(vec3(0,1,2) + a);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float star5(in vec2 p, in float r, in float rf) {
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= iResolution.x/iResolution.y;
  vec3 col = vec3(0.0, 0. ,0.0);

  for (int i=0; i<5;++i) { 
    vec2 p0 = p;
    float itime = iTime+1.2*float(i);
    p0.x += sin(itime);
    float b = itime;
    b = fract(b);
    b -= 0.5;
    b *= b;
    b *= 2.0;
    p0.y += b;

    p0 *= ROT(8.0*cos(itime));  
    float d = star5(p0, 0.5, 0.5);
    float od = abs(d)-0.015;

    if (d < 0.0) {
      col = palette(itime-8.0*d);
    }

    if (od < 0.0) {
      col = vec3(q.y);
    }
  }
  //col.x += sin(40.0*d);
  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}

float wave(float x, float y, float k) 
{
    return sin(10.0*x+10.0*y) / 5.0 +
           sin(20.0*x*k+15.0*y*k) / 3.0 +
           sin(4.0*x+10.0*y) / -4.0 +
           sin(y) / 2.0 +
           sin(x*x*y*20.0*k) + 
           sin(x * 20.0*k + 4.0) / 5.0 +
           sin(y * 30.0*k) / 5.0 + 
    	   sin(x) / 4.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec3 col1 = vec3(0.8, 0.1, 0.5);
    const vec3 col2 = vec3(0.1, 0.1, 0.8);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.xy *= 2.;
    uv.x += cos(iTime/100.)/4. - 0.5;
    uv.y += sin(iTime/100.)*2. -0.5;


    float z = wave(uv.x, uv.y, 1./10.) + 2.0;


    z *= 2.0 * (sin(iTime/20.)+2.);
    float d = fract(z);
    if(mod(z, 2.0) > 1.) d = 1.-d;

    vec3 col;
    for(float i=0.; i<3.; i++){
        col += vec3(step(d/fwidth(z*3.), 0.5+(iResolution.x+iResolution.y)/2000. - (i+1.)/3.)*((i+1.)/2.));
    }


    col *= mix(col1, col2, fwidth(z*4.)+0.3);
    fragColor = vec4(col, 1.);
}

