vec4 ut(vec4 a,vec1 b){return a*(1.-b);}
vec3 ut(vec3 a,vec1 b){return a*(1.-b);}
vec2 ut(vec2 a,vec1 b){return a*(1.-b);}
vec1 ut(vec1 a,vec1 b){return a*(1.-b);}
//below are the 5 alpha compositions:
vec4 pdOut (vec4 a,vec4 b){return ut(b,a.w);}
vec4 pdOver(vec4 a,vec4 b){return ut(b,a.w)+a;}
vec4 pdAtop(vec4 a,vec4 b){return ut(b,a.w)+(a*b.w);}
vec4 pdXor (vec4 a,vec4 b){return ut(b,a.w)+vec4(ut(a,b.w));}
