float random(vec2 st) {
    return fract(sin(dot(st,vec2(397.523,591.5939)))*53426.79);
}

float noise(vec2 st) {
    vec2 ip = floor(st);
    vec2 fp = fract(st);

    float a = random(ip);
    float b = random(ip + vec2(1., 0.));
    float c = random(ip + vec2(0., 1.));
    float d = random(ip + vec2(1., 1.));

    vec2 u = fp * fp * (3.0 - 2.0 * fp);
    return mix(mix(a, b, u.x), mix(c, d, u.x), u.y) * 2. - 1.;
}

float fbm(vec2 uv) {
    int LOOP = 6;
    float amplitude = 1.5;
    float frequency = .5;
    float ag = .5;
    float fg = 2.;

    float n = 0.;
    for (int i = 0; i < LOOP; i++) {
        n += noise(vec2(uv + n) * frequency + iTime) * amplitude;
        frequency *= fg;
        amplitude *= ag;
    }
    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(fbm(uv * 30.)) * vec3(.8, .9, .85);

    fragColor = vec4(col,1.);
}

