void calcRayForPixel( in vec2 pix, out vec3 resRo, out vec3 resRd )
{
	vec2 p = (2.0*pix-iResolution.xy) / iResolution.y;

     // camera movement	
	vec3 ro, ta;
	calcCamera( ro, ta );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

	resRo = ro;
	resRd = rd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord) / iResolution.y;

    float th = (iMouse.z>0.001) ? (2.0*iMouse.x-iResolution.x)/iResolution.y : 0.0;

	vec3 ro, rd, ddx_ro, ddx_rd, ddy_ro, ddy_rd;
	calcRayForPixel( fragCoord + vec2(0.0,0.0), ro, rd );
	calcRayForPixel( fragCoord + vec2(1.0,0.0), ddx_ro, ddx_rd );
	calcRayForPixel( fragCoord + vec2(0.0,1.0), ddy_ro, ddy_rd );

    // trace
	vec3 pos, nor;
	float occ;
    int mid;
    float t = intersect( ro, rd, pos, nor, occ, mid );

	vec3 col = vec3(BACKGROUND_VAL);
	if( mid!=-1 )
	{
#if 1
		// -----------------------------------------------------------------------
        // compute ray differentials by intersecting the tangent plane to the  
        // surface.		
		// -----------------------------------------------------------------------

		// computer ray differentials
		vec3 ddx_pos = ddx_ro - ddx_rd*dot(ddx_ro-pos,nor)/dot(ddx_rd,nor);
		vec3 ddy_pos = ddy_ro - ddy_rd*dot(ddy_ro-pos,nor)/dot(ddy_rd,nor);

		// calc texture sampling footprint		
		vec2     uv = texCoords(     pos, mid );
		vec2 ddx_uv = texCoords( ddx_pos, mid ) - uv;
		vec2 ddy_uv = texCoords( ddy_pos, mid ) - uv;
#else
		// -----------------------------------------------------------------------
        // Because we are in the GPU, we do have access to differentials directly
        // This wouldn't be the case in a regular raytracer.
		// It wouldn't work as well in shaders doing interleaved calculations in
		// pixels (such as some of the 3D/stereo shaders here in Shadertoy)
		// -----------------------------------------------------------------------
		vec2 uvw = texCoords( pos, mid );

		// calc texture sampling footprint		
		vec2 ddx_uvw = dFdx( uvw ); 
        vec2 ddy_uvw = dFdy( uvw ); 
#endif

		// shading		
		vec3 mate = vec3(0.0);

        if( p.x<th ) mate = vec3(1.0)*(1.0 - pristineGrid( uv - vec2(0.05), ddx_uv, ddy_uv, vec2(1.0/N) ));
        else         mate = vec3(1.0)*gridTextureGradBox( uv, ddx_uv, ddy_uv );

#ifdef INVERT_MAT
        mate = 1.0 - mate;
#endif

        // lighting	
		vec3 lin = doLighting( pos, nor, occ, rd );

        // combine lighting with material		
		col = mate * lin;

        // fog		
        col = mix( col, vec3(BACKGROUND_VAL), 1.0-exp( -0.00001*t*t ) );
	}

    // gamma correction	
	col = pow( col, vec3(0.4545) );

	col *= smoothstep( 1.0, 2.0, abs(p.x-th)/(2.0/iResolution.y) );

	fragColor = vec4( col, 1.0 );
}

/*
    Comparison of global tonemapping operators.

    Mouse to select an operator to show larger on top. Click on the red circle to
    return.

    Options:
        HUE_MODE:
            When 0, instead of mapping the sine wave from -1 to 1 to 0 to 1,
            it clips at 0 creating a sort of "worst case" for the Reinhard
            operator which operatres purely per-component, making fully saturated values of
            red, green, or blue look bad.
            When 1, it maps the -1 to 1 range to 0 to 1 "properly"

        SHOW_MORE:
            Show more operators

        IMAGE:
            Instead of a rainbow, show a psychedelic oil-slick-like moving image

    From left to right:
        when SHOW_MORE = 0:
        Clipped values:
            The simplest tonemapping operator which is no tonemapping operator, it just
            clips values above 1 to 1 per-component.
        Reinhard
            Another simple tonemapping operator, this time performing the operation
            x / (1.0 + x) per component.
        ACES
            From https://www.shadertoy.com/view/XsGfWV. Looks great!
        My tonemapping operator
            ACES-like.
            Each component going out is a combination of the three, allowing proper desaturation
            when used in combination with a Reinhard operator.
            I think the blue and green components also desaturate in a nicer looking manner,
            as the blue doesn't turn purple. Might also be cheaper as it only involves a single
            matrix multiplication rather than the two the ACES tonemapper uses to transform from
            RGB to XYZ and back. Also includes a contrast adjustment at the end to make it look
            more like the ACES operator.
            Plus, if you want to adjust the look, you can do so by changing the values in the
            matrix.

        when SHOW_MORE = 1:
            Clipped values
            Reinhard
            Reinhard extended(?)
                Instead of dividing by the components divide by the luminance
            ACES
            My tonemapping operator
            Reinhard ACES
                Like Reinhard but it mixes toward the luminance to achieve a similar look to ACES.
                Also made by me.
                Doesn't look super great IMO, might even be more expensive than the other one I
                made.

    Also I didn't mean for this to become so large oops ð
*/

#define HUE_MODE 0
#define SHOW_MORE 0
//#define IMAGE

#define lum(x) dot(x, vec3(0.2126, 0.7152, 0.0722))

vec3 gamma(vec3 c)
{
    return pow(c, vec3(0.4545));
}

vec3 clip(vec3 c)
{
    return clamp(c, 0.0, 1.0);
}

vec3 reinhard(vec3 c)
{
    return c / (1.0 + c);
}

vec3 reinhardExtended(vec3 c)
{
    return c / (1.0 + lum(c));
}

vec3 aces(vec3 color){	
	mat3 m1 = mat3(
        0.59719, 0.07600, 0.02840,
        0.35458, 0.90834, 0.13383,
        0.04823, 0.01566, 0.83777);
	mat3 m2 = mat3(
        1.60475, -0.10208, -0.00327,
        -0.53108,  1.10813, -0.07276,
        -0.07367, -0.00605,  1.07602);
	vec3 v = m1 * color;    
	vec3 a = v * (v + 0.0245786) - 0.000090537;
	vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
	return clamp(m2 * (a / b), 0.0, 1.0);	
}

vec3 reinhardAces(vec3 c)
{
    c = pow(c, vec3(1.5));
    vec3 g = vec3(lum(c));
    c = mix(g / (g + 1.0), c / (c + 1.0), exp2(-g*0.2));
    return c;
}

vec3 reinhardSpill(vec3 c)
{
    c *= mat3(vec3(1.000, 0.100, 0.025),
              vec3(0.075, 1.000, 0.050),
              vec3(0.030, 0.050, 1.000));
    c = 1.0 - exp(-c * 1.0);
    return 3.0*c*c-2.0*c*c*c;
}

vec3 hue(float x)
{
#if HUE_MODE == 0
    return max(vec3(0), sin((x+0.125+vec3(0.0, 0.33, 0.66)) * 6.2832));
#else
    return pow(sin((x+0.125+vec3(0.0, 0.33, 0.66)) * 6.2832)*0.5+0.5, vec3(3));
#endif
}

#define TMO(a, f) case a: c = f (c); break;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;

#if SHOW_MORE == 0
#define TMO_COUNT 3.0
#else
#define TMO_COUNT 5.0
#endif

#ifdef IMAGE
    vec3 c = hue(noise(uv, iTime)*5.0) / pow(abs(uv.y-0.5)*5.0, 2.0);
#else
    vec3 c = hue(uv.x*TMO_COUNT+iTime*0.2);
#endif
    float v = abs(uv.y*2.0-1.0);
    c /= v/(1.0-v);
    int tmo = int(fract(uv.x)*(TMO_COUNT+1.0));
    if(length(iMouse.xy) > 20.0 && uv.y > 0.5)
    {
        tmo = int(fract(m.x)*(TMO_COUNT+1.0));
    }

    switch(tmo)
    {
        TMO(0, clip);
#if SHOW_MORE == 1
        TMO(1, reinhard)
        TMO(2, reinhardExtended)
        TMO(3, aces)
        TMO(4, reinhardSpill)
        TMO(5, reinhardAces)
#else
        TMO(1, reinhard)
        TMO(2, aces)
        TMO(3, reinhardSpill)
#endif
    }

    c = mix(c, vec3(1, 0, 0), smoothstep(20.0, 18.0, length(fragCoord.xy)));
    fragColor = vec4(gamma(c),1.0);
}

