    // inputs	
    vec2 mo = iMouse.xy / iResolution.xy;
    if( iMouse.z<=0.00001 ) mo=vec2(0.0);

    gAnimTime = iTime;

	// montecarlo	
	vec3 tot = vec3(0.0);
    #if VIS_SAMPLES<2
	int a = 0;
	{
        vec2 p = -1.0 + 2.0*(fragCoord.xy) / iResolution.xy;
        p.x *= iResolution.x/ iResolution.y;
        float time = 4.0*iTime + 50.0*mo.x;
    #else
	for( int a=0; a<VIS_SAMPLES; a++ )
	{
		vec4 rr = texture( iChannel1, (fragCoord.xy+floor(256.0*hash2(float(a))))/iChannelResolution[1].xy );
        vec2 p = -1.0 + 2.0*(fragCoord.xy+rr.xz) / iResolution.xy;
        p.x *= iResolution.x/ iResolution.y;
		#if VIS_SAMPLES>3
        float time = 4.0*(iTime + 1.0*(0.4/24.0)*rr.w) + 50.0*mo.x;
		#else
        float time = 4.0*(iTime) + 50.0*mo.x;
		#endif
    #endif	

		// camera
        vec3  ro = cameraPath( time );
        vec3  ta = cameraPath( time+5.0 ); ta.y = ro.y - 5.5;
        float cr = 0.2*cos(0.1*time*0.5);

        // build ray
        vec3 ww = normalize( ta - ro);
        vec3 uu = normalize(cross( vec3(sin(cr),cos(cr),0.0), ww ));
        vec3 vv = normalize(cross(ww,uu));
        float r2 = p.x*p.x*0.32 + p.y*p.y;
        p *= (7.0-sqrt(37.5-11.5*r2))/(r2+1.0);
        vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

        // dof
        #if VIS_SAMPLES>2
        vec3 fp = ro + rd * 17.0;
        ro += (uu*(-1.0+2.0*rr.y) + vv*(-1.0+2.0*rr.w))*0.035;
        rd = normalize( fp - ro );
        #endif

        // render
        vec3 col = render( ro, rd );

		tot += col;
	}
	tot /= float(VIS_SAMPLES);

    // tone mapping			
    tot = tot*1.2/(1.0+tot);

	// gamma	
	tot = pow( clamp(tot,0.0,1.0), vec3(0.45) );

    tot = tot*tot*(3.0-2.0*tot);

	// vignetting	
	vec2 q = fragCoord.xy / iResolution.xy;
	tot *= 0.5 + 0.5*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.1 );

	fragColor = vec4( tot, 1.0 );
}    

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    vec3 cam = cameraPath( iTime );

    gAnimTime = iTime*0.2;

    vec3 ro = fragRayOri + cam + vec3(0.0,-2.0,0.0);
    vec3 rd = fragRayDir*vec3(-1.0,1.0,-1.0);
    vec3 col = render( ro, rd );

    // gamma	
	col = pow( clamp(col,0.0,1.0), vec3(0.44) );

    fragColor = vec4( col, 1.0 );
}

