/*
Complex differential multiplication
*/
vec2 M(vec2 a, vec2 b) { 
    return vec2( 
        dot(a, vec2(b.x, -b.y)), 
        dot(a, b.yx)
    ); 
}
mat2 M(mat2 a, mat2 b) {
    return mat2(
        M(a[0], b[0]),
        M(a[0], b[1]) + M(a[1], b[0])
    );
}


void mainImage(out vec4 o, vec2 i) {
    // fixed point f(1 + 0i) = 1 + 0i
    mat2 z, a = mat2(vec2(1, 0), vec2(0));

    vec2 w, t, r = iResolution.xy,
         m = 2. * iMouse.xy - r;

    // mandelbrot condition
    z[0] = t = i = (i + i - r) / r.x;
    // derivative of a variable is 1
    z[1] = vec2(1);

    // julia iteration
    float j, f, k = 9.;
    for (m /= r.x; j++ < 99.; f < k ? k = f, t = z[0] : t) {
        // nÂ²ââ pentation is n^n^n..., n times.
        // fractional iteration by taylor
        // expansion around the fixed point.
        mat2 c = z - a;
        z += M(M(c, c), z) 
           + M(M(z, c) * 6. + z * 3.,
               M(M(c, c), c) / 6.);
        w = z[0];

        // controls
        z[0] += mix(i, m, max(0., sign(iMouse.z)));

        // escape condition
        f = dot(z[0], z[0]);
        if (f > 4.) break;
    }

    // proportional to distance
    float d = log(sqrt(f)) * sqrt(f / dot(z[1], z[1])),
          // grid
          g = smoothstep(0., 4., length(cos(w))),
          // hue angle
          h = atan(t.y, t.x);

    // color mapping
    vec3 v = vec3(h - 2.1, h, h + 2.1);

    o = vec4(.5 + .5 * cos(v), 1.);
    o = smoothstep(.1, .0, o * d + .1 * g);
}


