#define R (iResolution.xy)
#define M_PI 3.1415
#define TAU (M_PI*2.0)
#define ZERO min(0, iFrame)
#define NEAR 0.003
#define FAR 32.0
#define STEPS 100
#define SHADOW_STEPS 8
#define AMBIENT 0.07

struct Data {
    vec3 p;
    vec3 n;
    float d;
    int id;
};

#define NEW_DATA Data(vec3(0.0), vec3(0.0), FAR, -1)

struct Light {
    vec3 p;
    vec3 c;
    float s;
};

mat2 rot(float a) { float c = cos(a); float s = sin(a); return mat2(c, s, -s, c);}

vec3 pointRot(in vec3 p, in vec3 r) {
    p.yz *= rot(r.x);
    p.xz *= rot(r.y);
    p.xy *= rot(r.z);
    return p;
}

float boxSDF(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.0)) + min(max(p.x, max(p.y, p.z)), 0.0);
}

float planeSDF(vec3 p, vec2 s) {
    return max(max(p.y, abs(p.x)-s.x), abs(p.z)-s.y)+
    0.019*cos(p.x*10.+p.z*10.+iTime*10.) + 
    0.025*sin(p.x+p.z*10.+iTime*10.) + 
    0.025*exp(cos(p.x*p.z*10.+sin(iTime+p.x*3.14+p.z))-1.) - 
    0.025*exp(sin((iTime*2.2)+(p.x*10.-p.z*10.))-1.);
}

#define ID_BOX 0
#define ID_GROUND 1

float sdf(vec3 p, inout Data data) {
    float box = boxSDF(pointRot(p - vec3(0, (0.19) + 0.25*(0.5+0.5*sin(iTime)), 0), vec3(iTime, iTime, 0)), vec3(0.1))-0.01;
    float ground = planeSDF(p, vec2(2, 2));

    float d = FAR;
    if (box < d) { d = box; data.id = ID_BOX; }
    if (ground < d) { d = ground; data.id = ID_GROUND; }

    return d/1.1;
}

bool march(vec3 ro, vec3 rd, inout Data data) {
    float d = 0.0;

    for (int i = ZERO; i < STEPS; i++) {
        vec3 p = ro+rd*d;
        float next = sdf(p, data);

        if (abs(next) <= NEAR) break;
        if (abs(d) >= FAR) return false;

        d += next;
    }

    vec3 p = data.p = ro+rd*d;
    data.d = d;
    vec2 e = vec2(0.003, 0.0);
    data.n = normalize(sdf(p, data) - vec3(
        sdf(p - e.xyy, data),
        sdf(p - e.yxy, data),
        sdf(p - e.yyx, data)
    ));


    return true;
}

float getShadow(vec3 ro, vec3 rd, in Data data, in Light light) {
    vec3 L = normalize(light.p);
    vec3 N = data.n;
    ro = data.p + N*NEAR*3.;
    rd = L;

    float d = 2.5*max(0.0, -dot(N, L));

    for (int i = ZERO; i < SHADOW_STEPS; i++) {
        vec3 p = ro+rd*d;
        float r = float(i) / float(SHADOW_STEPS);
        p += N * (1.0/(1.0 + r))*0.1;
        float next = sdf(p, data);

        if (abs(next) <= NEAR || abs(d) >= FAR) break;

        d += next;
    }

    float s = (d / (1.0 + d));
    return clamp(smoothstep(0.0, 0.5, s*s), AMBIENT*2., 1.0);
}


vec3 getColor(in Data data) {
    switch (data.id) {
        case ID_BOX: return vec3(1, 0, 0); break;
        default: return vec3(1.0); break;
    }

    return vec3(1.0);
}

vec3 forEachLight(vec3 ro, vec3 rd, inout Data data, in Light light) {
    vec3 L = normalize(light.p);
    vec3 N = data.n;
    vec3 ref = reflect(L, N);
    float NdotL = max(AMBIENT, dot(N, L));
    float VdotR = max(0.0, dot(rd, ref));
    float spec = pow(VdotR, 16.0);
    float shadow = NdotL <= (AMBIENT*0.03) ? AMBIENT : getShadow(ro, rd, data, light);
    vec3 diffuse = getColor(data) / M_PI;
    return (NdotL * light.c + spec)*shadow*diffuse;
}

vec3 render(vec3 ro, vec3 rd, inout Data data) {
    Light light = Light(vec3(1, 2., -3.), vec3(0.97, 0.69, 0.51), 2.0);
    vec3 L = normalize(light.p);
    float VdotL = max(0.0, dot(rd, L));
    vec3 col = vec3(0.0);
    col += pow(VdotL, 4.)*light.c;
    if (march(ro, rd, data)) {
        col += forEachLight(ro, rd, data, light);
    }

    return col;
}

void mainImage(out vec4 O, in vec2 fc)
{
    vec3 col = vec3(0.0);
    vec2 uv = (fc.xy - 0.5 * R.xy) / R.y;
    vec4 m = vec4((iMouse.xy - 0.5 * R.xy) / R.y, iMouse.zw);

    vec3 ro = vec3(0.0, 0.2, -1.);
    vec3 rd = normalize(vec3(uv.xy, 1.0));

    if (m.z < 0.01) {
        m.x = (iTime+4.0)*0.09;
        m.y = -0.09;
    }

    rd.yz *= rot(m.y*TAU);
    rd.xz *= rot(m.x*TAU);
    ro.yz *= rot(m.y*TAU);
    ro.xz *= rot(m.x*TAU);



    Data data = NEW_DATA;
    col += render(ro, rd, data);
    float depth = data.d / FAR;
    col += smoothstep(0.02, 0.2, depth);
    col += col*col;

    col = pow(col, vec3(1.0 / 2.2));

    O = vec4(col, 1.0);
}

// Ray marching constants
#define MAX_STEPS 350
#define SURF_DIST 0.0001
#define MAX_DIST 100.0
#define PI 3.141592653592
#define TAU 6.283185307185


////////////////////////////////////////////////////////////////
// Signed Distance Functions
////////////////////////////////////////////////////////////////
float sdPlane( vec3 p, vec3 n, float h )
{
  return dot(p,normalize(n)) + h;
}

float sdSphere( vec3 p, vec3 c, float r )
{
    return length(p-c) - r;
}

float sdRoundedBox(vec3 p, vec3 s, float r) {
    p = abs(p)-(s-r);
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.) - r;
}

float sdEllipsoid( vec3 po, vec3 c, vec3 r )
{
    vec3 p = po-c;

    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

////////////////////////////////////////////////////////////////
// SDF Operations
////////////////////////////////////////////////////////////////

float opUnion(float d1, float d2 ) { return min(d1,d2); }

float opSubtraction(float d1, float d2) {
    //NOTE: Flipped order because it makes more sense to me
    return max(-d2, d1);
}
float opIntersection(float d1, float d2) {
    return max(d1, d2);
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d1, -d2, h ) + k*h*(1.0-h);
}
float opSmoothIntersection(float d1, float d2, float k){
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

////////////////////////////////////////////////////////////////
// Rotations
////////////////////////////////////////////////////////////////


vec3 rotX(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        1., 0., 0.,
        0., c, -s,
        0., s, c
        );
    return m * p;
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
        );
    return m * p;
}

vec3 rotZ(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, -s, 0.,
        s, c, 0.,
        0., 0., 1.
        );
    return m * p;
}
vec3 orbitControls(vec3 po) {
    vec2 m = (vec2(iMouse.x, iMouse.y) / iResolution.xy) + 0.5;
    vec3 p = po;
    p = rotY(po, -m.x*TAU - PI);
    p = rotX(p, -m.y*PI - PI / 2.0);
    return p;
}


////////////////////////////////////////////////////////////////
// Ray Marching Functions
////////////////////////////////////////////////////////////////

float body1(vec3 p) {
    float f = 0.5;
    float r = 0.1;
    float R = 0.375;

    float d = sdSphere(p, vec3(0.45 * sin(f * iTime), 0.5 * cos(f * iTime + PI / 2.), 0.), R);
    return d;
}

float body2(vec3 p) {
    float f = 0.5;
    float R = 0.375;

    vec3 center = p - vec3(0.45 * cos(f * iTime), 0.5 * sin(f * iTime), 0.);
    center = rotY(center, 0.25 * iTime);
    center = rotX(center, 0.25 * iTime);

    float d = sdRoundedBox(center, vec3(0.3), 0.05);
    return d;
}

float body3(vec3 p) {
    float f = 0.5;
    float R = 0.375;

    vec3 center = p - vec3(0.5 * cos(f * iTime));
    center = rotZ(center, 0.25 * iTime);
    float d = sdRoundedCylinder(center, 0.125, 0.1, 0.5);

    return d;
}

vec2 map(vec3 p) {
    // Mouse
    vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;  
    vec2 res;
    p = rotY(p, -2.*m.x);
    float r = 0.1;

    float s1 = sdSphere(p, vec3(-0.625, -0.25, 0.), 0.625);
    res = vec2(s1, 0.0); // id to track color

    float s2 = sdSphere(p, vec3(0.625, 0.25, 0.), 0.625);
    if (s2 < res.x) { res.y = 1.0; };
    res.x = opSmoothUnion(res.x, s2, r);



    return res;
}

vec3 rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    float mat = 1.0;
    float minDist = MAX_DIST;
    float lastDist = 0.;
    float dir = -1.0;
    bool inc = false;
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec3 p = ro + rd * d;
        vec2 ds = map(p);
        d += ds.x;

        float dd = ds.x - lastDist;

        lastDist = ds.x;

        if (sign(dd) > 0.) {
            minDist = min(minDist, ds.x);
        };

        mat = ds.y;

        if (d >= MAX_DIST || ds.x < SURF_DIST) break;
        i++;
    }
    return vec3(d, mat, minDist);
}

vec3 gradient(vec3 p) {
    float epsilon = 0.0001;
    vec3 dx = vec3(epsilon, 0., 0.0);
    vec3 dy = vec3(0., epsilon, 0.0);
    vec3 dz = vec3(0., 0.0, epsilon);

    float ddx = map(p + dx).x - map(p - dx).x;
    float ddy = map(p + dy).x - map(p - dy).x;
    float ddz = map(p + dz).x - map(p - dz).x;

    return normalize(vec3(ddx, ddy, ddz));
}

vec3 rayDirection(vec2 p, vec3 ro, vec3 rt) {

    // screen orientation
    vec3 vup = vec3(0., 1.0, 0.0);
    float aspectRatio = iResolution.x / iResolution.y;

    vec3 vw = normalize(ro - rt);
    vec3 vu = normalize(cross(vup, vw));
    vec3 vv = cross(vw, vu);
    float theta = radians(30.); // half FOV
    float viewport_height = 2. * tan(theta);
    float viewport_width = aspectRatio * viewport_height;
    vec3 horizontal = -viewport_width * vu;
    vec3 vertical = viewport_height * vv;
    float focus_dist = length(ro - rt);
    vec3 center = ro - vw * focus_dist;

    vec3 rd = center + p.x * horizontal + p.y * vertical - ro;

    return normalize(rd);
}

vec2 array(vec2 p, vec2 spacing) {

    return mod(p, spacing);

}

float Circle(vec2 p, vec2 c, float r) {
    return length(p-c) - r;
}

float dots(vec2 p, float spacing, float rad, float fac) {

    //float spacing = 0.04;
    //float rad = 0.2 * spacing;

    vec2 r = array(p, vec2(spacing));

    float c = Circle(r, vec2(spacing * 0.5), rad);

    return c - fac;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0; // normalizing

    // Ray Marching
    vec3 rt = vec3(0., 0., 0.);
    vec3 ro = vec3(0., 0., -10.02);

    vec3 rd = rayDirection(uv, ro, rt);
    vec3 d = rayMarch(ro, rd);

    // Background
    float v = length(uv) * .75;
    fragColor = vec4(0.1,0.7,1.,1.);
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.y)));
    fragColor += vec4(mix(0.2, 0.4, smoothstep(0.0, 1.0, uv.x)));

    vec3 color;

    if (d.x <= 100.0) {
        vec3 p = ro + rd * d.x;
        vec3 pr = orbitControls(p);
        vec3 N = gradient(p);
        float light1 = dot(N, normalize(vec3(1, 1,-1)))*.5+.5;
        float light2 = dot(N, normalize(vec3(-1, 1,-1)))*.5+.5;
        vec3 illumination = 0.5 * vec3(light1) + 0.5 * vec3(light2);

        if (d.y == 0.0) color = vec3(0.2,0.8,1.);
        if (d.y == 1.0) color = vec3(0.8,0.2,1.);
        if (d.y == 2.0) color = vec3(0.8,0.,0.);
        color *= illumination; 


        // halftone
        //float spacing = 0.025;
        //vec2 uv2 = (fragCoord - 0.5*iResolution .xy)/iResolution.y;
        //float shadeFac = 0.5*(N.z+1.);
        //float dotMax = 0.01;
