        //float minRad = 0.2 * spacing; 
        //float dts = dots(uv2, spacing, minRad, dotFac);
        //dts *= 255.;

        // Specular highlights
        vec3 R = reflect(normalize(vec3(1.,0.,0.)), N);
        vec3 specular = vec3(1.0) * pow(max(dot(R, rd), 0.0),10.0);
        color += specular * 0.08;

        // fake fresnel
        float nDotV = dot(N, rd) + 1.;
        color += nDotV * nDotV * 0.3;        

        fragColor = vec4(color, 1.0);

    }

        // outline

        if (d.z < 0.015 && d.z > SURF_DIST) {
            vec3 outlineColor = vec3(0.0,0.,0.);
            color = mix(color, outlineColor, smoothstep(1., 0., d.z));
