}

vec3 palette( in float t )
{
    vec3 a = vec3(1.0, 1.0, 1.0);   // Set a to pure white for brightness.
    vec3 b = vec3(0.7, 0.9, 1.0);   // Adjust b for neon-like colors.
    vec3 c = vec3(2.0, 2.0, 2.0);   // Increase c for faster oscillation.
    vec3 d = vec3(0.0, 0.2, 0.4);   // Adjust d for the desired phase.

    return a + b * cos(6.28318 * (c * t + d));
}

// Narkowicz 2015, "ACES Filmic Tone Mapping Curve"
vec3 aces(vec3 x) {
  const float a = 2.51;
  const float b = 0.03;
  const float c = 2.43;
  const float d = 0.59;
  const float e = 0.14;
  return clamp((x * (a * x + b)) / (x * (c * x + d) + e), 0.0, 1.0);
}

float rand(vec2 st) 
{
    return fract(sin(dot(st, vec2(12.1524, 78.46584))) * 45434.56454);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Smooth random values at four corners of the cell
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Interpolate between those random values
    vec2 u = f * f * (3.0 - 2.0 * f); // Smooth interpolation function
    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    uv += (10.0 * vec2(sin(iTime * 0.01), cos(iTime * 0.01)));
    uv = abs(uv) + noise(uv * 5.0 + sin(iTime)) * 0.1;
    uv *= vec2(1.0) + noise(uv) * 0.01;



    vec3 col = vec3(0.0);
    vec3 col2 = vec3(0.0);


    for (float i = 0.0; i < 3.0; i ++)
    {
        uv = fract(uv * 1.5) - 0.5;

        float r = abs(sin((abs(sin(iTime)) + 1.0) * 1.5));
        float f = (length(uv * 3.0) - r) - 0.1;


        float tm = iTime;
        f = abs(sin(f + tm * 0.51) - cos(f + tm * 0.5)) * 4.5;

        f = pow(0.1 / f, 1.9);

        col += palette(noise(vec2(
                            fract(length(uv) + sin(iTime * 0.7416)),
                            fract(1.0 / length(uv) + sin(iTime * 0.476))
                        ))) * f;
    }

    col = aces(col);
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col,1.0);
}

