}

float line(vec2 p, vec2 a, vec2 b) // https://iquilezles.org/articles/distfunctions2d/
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec3 colorize(ivec2 id)
{
    vec3 color = hash32(vec2(id));
    if(((id.x^id.y)&1)==0)
        color = 1.0 - color;
    return color;
}



