    vec2 start = center;
    vec2 end = center+0.33333*vec2(cos(iTime), sin(iTime));
    float f = smoothstep(0.5, 0.0, distance(uv, end));
    start = mix(
    start,start+end*vec2(cos(iTime+uv.x+60.*iTime*uv.y), sin(iTime+uv.y+60.*iTime*uv.x)),f);
    float pf = dot(uv, start-end);
    vec3 gc = vec3(abs(pf), f, abs(f-pf));
    vec2 pa = uv - start; vec2 ba = end - start; 
    fragColor = vec4(clamp(1.0 - length(pa - ba * clamp(
        dot(pa, ba) / dot(ba, ba), 0.0, 1.0))/0.05, 0.0, 1.0)*mix(vec3(1.0), gc, f),1.0);
}

/// by Dom Mandy in 2023


/*
domrally's flatstep function.
Kinda like a smoothest-step or â degree hermite interpolation.
*/
vec2 flatstep(float a, float b, float x) {
    vec2 f;
    float t = clamp((x - a) / (b - a), 9e-8, 1. - 9e-8),
          c = cos(3.14 * t),
          h = atanh(c),
          H = h * h;

    // error function method by Sergei Winitzki using PadÃ© approximations
    f[0] = .5 - .5 * sign(h) * sqrt(1. - exp(-H * (.1 * H + 1.27) / (.1 * H + 1.)));
    // derivative
    f[1] = sin(t) * exp(-H) / (c * c - 1.);

    return f;
}


void mainImage(out vec4 o, vec2 i) {
    vec2 r = iResolution.xy,
         f = flatstep(.5, r.x - .5, i.x + .5);

    // 1st order gradient based distance approximation
    float d = abs((f.x + .326 - 1.6 * i.y / r.y) / (4. + f.y * f.y));
    // antialiasing
    o = vec4(smoothstep(1.5 / r.y, 0., max(0., d - 6. / r.y)));
    // ripples
    o += vec4((.4 + .1 * cos(d * 2e2 - iTime)) / (1. + 9. * d), 0, 0, 1);
}


